--

INSERT INTO `tbl_documents` (`doc_id`, `doc_title`, `doc_content`, `doc_type`, `doc_vip`, `doc_summary`) VALUES
(1, 'Hướng dẫn lắp ráp mạch nạp Acquy tự động', '<p><strong>H&acirc;n hạnh giới thiệu đến c&aacute;c bạn một mạch nạp c&oacute; khả năng tự động ngắt mạch khi b&igrave;nh đầy nhằm bảo vệ tốt nhất cho ắc quy .</strong></p>\r\n\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p>&nbsp;Tr&ecirc;n thực tế th&igrave; mạch nạp cho ắc quy rất đơn giản chỉ cần một con diot chỉnh lưu l&agrave; đủ , nhưng như thế rất hại cho b&igrave;nh khi đ&atilde; đầy điện m&agrave; vẫn nạp tiếp.</p>\r\n\r\n<p>Dựa tr&ecirc;n cơ sở so s&aacute;nh điện &aacute;p của ic khuếch đại thuật to&aacute;n đối chiếu với lưu lượng điện trong b&igrave;nh m&agrave; mạch dưới đ&acirc;y c&oacute; khả năng nạp tự động điều chỉnh lượng điện &aacute;p nạp cho b&igrave;nh .<img alt="Hướng dẩn lắp ráp mạch nạp Acquy tự động" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui1.jpg" style="height:162px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Nguy&ecirc;n l&yacute; hoạt động rất đơn giản như sau :</strong></p>\r\n\r\n<p>Diot zen tạo điện &aacute;p tham chiếu ở đ&acirc;y sử dụng dieenot zen 6V . Nhờ diot n&agrave;y m&agrave; điện &aacute;p tham chiếu v&agrave;o ch&acirc;n 2 lu&ocirc;n được giữ cố định d&ugrave; b&igrave;nh đ&atilde; vơi điện .<br />\r\nBiến trở v&agrave; c&aacute;c điện trở ph&acirc;n &aacute;p v&agrave;o ch&acirc;n số 3 lấy điện &aacute;p thực tế của b&igrave;nh so s&aacute;nh với điện &aacute;p tham chiếu ở ch&acirc;n số 2 . Khi điện &aacute;p của b&igrave;nh chưa đầy điện &aacute;p ở ch&acirc;n số 3 nhỏ hơn điện &aacute;p ch&acirc;n 2 đầu ra khuếch đại thuật to&aacute;n ở mức 0 , role chưa c&oacute; điện &aacute;p k&iacute;ch mở , d&ograve;ng điện được nạp ở b&igrave;nh nhờ điot cầu . Khi b&igrave;nh đầy ch&acirc;n ch&acirc;n số 3 điện &aacute;p lớn hơn điện &aacute;p so s&aacute;nh , l&uacute;c n&agrave;y ch&acirc;n 6 cấp d&ograve;ng k&iacute;ch mở tran đ&oacute;ng điện cho role , khi đ&oacute; sẽ c&aacute;ch ly b&igrave;nh với d&ograve;ng nạp , đ&egrave;n led b&aacute;o s&aacute;ng , khi đ&oacute; ta c&oacute; thể ngắt b&igrave;nh ra được ,</p>\r\n\r\n<p>Ch&uacute; &yacute; b&igrave;nh ắc quy đầy ở điện &aacute;p tầm 13,7V sử dụng biến trở điều chỉnh đ&uacute;ng điện &aacute;p sao cho role tự ngắt ở điện &aacute;p n&agrave;y ,<br />\r\nnếu k c&oacute; diot zen 6v th&igrave; d&ugrave;ng 3 v vẫn được nhưng cần ph&acirc;n &aacute;p lại<br />\r\nic ở đ&acirc;y l&agrave; Lm741 c&oacute; thể thay thế bằng c&aacute;c loại kh&aacute;c như Lm358, 324&hellip; cần x&aacute;c định đ&uacute;ng ch&acirc;n trước khi sử d&ugrave;ng.<br />\r\nD&ograve;ng nạp nhỏ hơn 1/10 dung lượng ắc quy sẽ k&eacute;o d&agrave;i tuổi thọ của b&igrave;nh , vd b&igrave;nh 25a th&igrave; biến &aacute;p chọn loại 3a l&agrave; hợp l&yacute;.<br />\r\nRole se k đ&oacute;ng ngay lập tức m&agrave; từ từ do đ&oacute; sẽ điều chỉnh được d&ograve;ng nạp hợp l&yacute; ta kh&ocirc;ng cần băn khoăn về điều n&agrave;y</p>\r\n\r\n<p><strong>Mạch in :</strong></p>\r\n\r\n<p><img alt="Hướng dẩn lắp ráp mạch nạp Acquy tự động" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui2.jpg" style="height:211px; margin:0px; width:450px" /></p>\r\n</div>', 'trick', 1, 'Hân hạnh giới thiệu đến các bạn một mạch nạp có khả năng tự động ngắt mạch khi bình đầy nhằm bảo vệ tốt nhất cho ắc quy.'),
(2, 'Thiết kế mạch Demo led cube 8x8x8', '<p><strong>Bản demo led cube 8x8x8 . thử tr&ecirc;n 89S52 , ch&uacute;ng t&ocirc;i khuy&ecirc;n c&aacute;c bạn kh&ocirc;ng n&ecirc;n l&agrave;m ledcube với loại vdk n&agrave;y , thứ nhất tr&igrave;nh bi&ecirc;n dich kh&ocirc;ng hỗ trợ mảng nhiều chiều tốt , đưa mảng [10][8] tr&igrave;nh keliC b&aacute;o lỗi tr&agrave;n bộ nhớ trong khi với AVR viết bao nhi&ecirc;u t&ugrave;y th&iacute;ch .</strong></p>\r\n\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p>Thứ 2 quan trọng nhất l&agrave; bộ nhớ với việc xuất 64 bit một l&uacute;c th&igrave; chắc tầm 5,6 hiệu ứng l&agrave; full rồi. Ở bản demo n&agrave;y&nbsp;chu1ng t&ocirc;i&nbsp;sử dụng 74hc595 qu&eacute;t cột v&agrave; uln2803 qu&eacute;t lớp , với 8x8x8 ta c&oacute; thể sử dụng th&agrave;nh một led matrix để chạy chữ,.Chế độ qu&eacute;t giống như matrix 8&times;8 tự update code khi thay đổi chuỗi k&yacute; tự (chi tiết xem video ph&iacute;a dưới).</p>\r\n\r\n<p>Phi&ecirc;n bản thử nghiệm tr&ecirc;n 89s52 . C&ograve;n đ&acirc;y l&agrave; một số h&igrave;nh ảnh về bản demo n&agrave;y :</p>\r\n\r\n<p><strong>Khối led :</strong></p>\r\n\r\n<p><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00160.jpg" style="height:360px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00161.jpg" style="height:338px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Sơ đồ nguy&ecirc;n l&yacute; :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00162.jpg" style="height:245px; margin:0px; width:450px" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mạch in :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00163.jpg" style="height:218px; margin:0px; width:450px" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mạch thực tế :</strong></p>\r\n\r\n<p><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00164.jpg" style="height:338px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>lập tr&igrave;nh :</strong>&nbsp;<img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00165.jpg" style="height:338px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Video demo :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bản ch&iacute;nh thức sẽ ra mắt trong thời gian sớm nhất .&nbsp;</p>\r\n</div>', 'trick', 1, 'Đây là bản demo led cube 8x8x8 . thử trên 89S52 , chúng tôi khuyên các bạn không nên làm ledcube với loại vdk này , thứ nhất trình biên dich không hỗ trợ mảng nhiều chiều tốt , đưa mảng [10][8] trình keliC báo lỗi tràn bộ nhớ trong khi với AVR viết bao nhiêu tùy thích.'),
(3, 'Mạch nghịch lưu 1 pha 12V to 220V', '<p><strong>Như ch&uacute;ng ta đ&atilde; biết để việc tạo ra d&ograve;ng điện l&agrave; bước tiến vĩ đại trong lịch sử lo&agrave;i người .Để sản xuất ra d&ograve;ng điện xoay chiều c&oacute; rất nhiều phương ph&aacute;p như thủy điện,nhiệt điện .</strong></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image001_1.jpg" style="height:417px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Phần I</strong><br />\r\n<em><strong>Cơ sở l&yacute; thuyết</strong></em></p>\r\n\r\n<p>Nh&igrave;n chung c&aacute;c phương ph&aacute;p tr&ecirc;n &aacute;p dụng cho sản xuất điện c&ocirc;ng nghiệp , c&ograve;n với c&aacute;c hộ gia đ&igrave;nh khi nguồn điện c&ocirc;ng nghiệp bị cắt phương ph&aacute;p chủ yếu được sử dụng ở đ&acirc;y l&agrave; nghịch lưu d&ograve;ng điện một chiều th&agrave;nh xoay chiều. Dựa tr&ecirc;n phương ph&aacute;p đ&oacute; nh&oacute;m đưa ra sơ đồ nguy&ecirc;n l&yacute; tạo điện &aacute;p xoay chiều từ d&ograve;ng một chiều.</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image002_1.jpg" style="height:203px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>I,Khối nguồn</strong><br />\r\nNguồn điện được sử dụng ở đ&acirc;y l&agrave; nguồn điện một chiều lấy từ b&igrave;nh ắc quy .Thời gian sử dụng phụ thuộc chủ yếu v&agrave;o dung<br />\r\nlượng lưu trữ của ắc quy .C&ocirc;ng thức t&iacute;nh c&ocirc;ng suất ph&aacute;t của acquy như sau: P=u.i<br />\r\nv&iacute; dụ: acquy 12v 100Ah th&igrave; c&ocirc;ng suất ph&aacute;t :12.100=1200w<br />\r\nNếu chạy b&oacute;ng đ&egrave;n compac 20w sẽ được 60 giờ .</p>\r\n\r\n<p><strong>II, Khối tạo tần số 50Hz</strong><br />\r\nNhiệm vụ của khối tạo ra s&oacute;ng dao động đưa v&agrave;o khối c&ocirc;ng suất với tần số điện c&ocirc;ng nghiệp. S&oacute;ng ở đ&acirc;y thường l&agrave; hai dạng ch&iacute;nh l&agrave; h&igrave;nh sin hoặc vu&ocirc;ng .Thường th&igrave; khối c&ocirc;ng suất trở kh&aacute;ng đầu v&agrave;o rất nhỏ n&ecirc;n tr&ecirc;n thực tế ch&uacute;ng ta cần một khối khuyếch đại đệm nhiệm vụ ổn định khối ph&aacute;t xung dao động giảm trở kh&aacute;ng đầu v&agrave;o cho tầng c&ocirc;ng suất.</p>\r\n\r\n<p><strong>III,Khối c&ocirc;ng suất</strong><br />\r\nTừ dạng s&oacute;ng nhận được từ khối ph&aacute;t khối c&ocirc;ng suất sẽ khuyếch đại đưa đến biến &aacute;p tạo điện &aacute;p xoay chiều. Thường th&igrave; khối n&agrave;y sử dụng c&aacute;c linh kiện c&ocirc;ng suất như thysistor transistor chịu d&ograve;ng lớn như D718,2N3055&hellip;.y&ecirc;u cầu cho khối n&agrave;y hoạt động tốt cần c&oacute; hệ thống tản nhiệt l&agrave;m m&aacute;t.</p>\r\n\r\n<p>IV,Biến &aacute;p nghịch lưu<br />\r\nĐ&acirc;y l&agrave; th&agrave;nh phần ch&iacute;nh quyết định tới c&ocirc;ng suất ph&aacute;t của mạch. Biến &aacute;p được sử dụng l&agrave; biến &aacute;p nghịch lưu c&oacute; tỷ số v&ograve;ng d&acirc;y của cuộn thứ cấp lớn hơn rất nhiều cuộn sơ cấp.C&ocirc;ng suất của mạch được t&iacute;nh như sau: Pmax=U.I<br />\r\nVới I l&agrave; d&ograve;ng điện biến &aacute;p chịu được<br />\r\nU l&agrave; hiệu điện thế đặt v&agrave;o cuộn sơ cấp<br />\r\nV&iacute; dụ một biến &aacute;p nghịch lưu 12V220V d&ograve;ng điện 40A<br />\r\nC&ocirc;ng suất tối đa của mạch sẽ l&agrave;:12.40=480W chạy được một ti vi 2 quạt v&agrave; 3 b&oacute;ng t&iacute;p 40W</p>\r\n\r\n<p><strong>Phần II</strong></p>\r\n\r\n<p><em><strong>Sơ đồ mạch</strong></em></p>\r\n\r\n<p>C&aacute;c bạn c&oacute; thể thực hiện theo 1 trong c&aacute;c sơ đồ sau :</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/dientu00045.jpg" style="height:265px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00046_1.jpg" style="height:244px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00047.jpg" style="height:293px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00048.jpg" style="height:264px; margin:0px; width:450px" /></p>\r\n\r\n<p>C&ograve;n đ&acirc;y l&agrave; mạch m&igrave;nh thiết kế trong đồ &aacute;n :</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image005.jpg" style="height:204px; margin:0px; width:510px" /></p>\r\n\r\n<p>Ch&uacute;ng ta cần quan t&acirc;m đến IC CD4047<br />\r\n<strong>Chức năng:</strong>&nbsp;tạo s&oacute;ng vu&ocirc;ng hai nửa chu kỳ.<br />\r\nĐ&acirc;y l&agrave; ic gồm 14 ch&acirc;n đ&oacute;ng g&oacute;i dạng dip 100T. Điện &aacute;p hoạt động trong khoảng từ 3 đến 15 V. Ch&uacute;ng ta cần quan t&acirc;m tới chức năng của c&aacute;c ch&acirc;n sau:<br />\r\nCh&acirc;n 1 đầu v&agrave;o tụ C<br />\r\nCh&acirc;n 2 đầu v&agrave;o điện trở R<br />\r\nCh&acirc;n 3 đầu v&agrave;o R-C tạo dao động với tần số định sẵn<br />\r\nCh&acirc;n 10 đầu ra xung vu&ocirc;ng b&aacute;n chu kỳ dương<br />\r\nCh&acirc;n 11 đầu ra xung vu&ocirc;ng b&aacute;n chu kỳ &acirc;m<br />\r\nCh&acirc;n 7 cấp nguồn &acirc;m<br />\r\nCh&acirc;n 14 cấp nguồn dương<br />\r\nTần số của xung vu&ocirc;ng ra được t&iacute;nh theo c&ocirc;ng thức:</p>\r\n\r\n<p>T= 2,.48RC<img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00049.jpg" style="height:180px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Ph&acirc;n t&iacute;ch mạch điện :</strong><br />\r\nĐầu ti&ecirc;n IC 4047 ph&aacute;t ra s&oacute;ng vu&ocirc;ng với hai nửa chu kỳ.Với C=0,1uF ta điều chỉnh biến trở để tần số hoạt động l&agrave; 50Hz .<br />\r\nT&iacute;n hiệu s&oacute;ng vu&ocirc;ng được xuất ra tr&ecirc;n c&aacute;c ch&acirc;n 10,11 k&iacute;ch mở tran H1061,ở đ&acirc;y n&oacute; hoạt động như một kh&oacute;a điện tử.<br />\r\nTrasistor H1061 l&agrave; tran c&ocirc;ng suất tầm trung nhiệm vụ trong mạch k&iacute;ch mở tầng tran c&ocirc;ng suất lớn. Cụ thể khi c&oacute; t&iacute;n hiệu từ Ic lm358 đưa v&agrave;o tran mở khi đ&oacute; điện &aacute;p của t&iacute;n hiệu được khuyếch đại đủ để k&iacute;ch mở cặp tran D718<br />\r\nCặp tran c&ocirc;ng suất D718 hoạt động như một kh&oacute;a điện tử đ&oacute;ng ngắt li&ecirc;n tục với tần số 50Hz của bộ ph&aacute;t xung .Từ sự đ&oacute;ng ngắt n&agrave;y d&ograve;ng điện qua biến &aacute;p thay đổi theo dạng t&iacute;n hiệu s&oacute;ng vu&ocirc;ng .Ở chu kỳ dương cuộn L1 của phần sơ cấp được cấp nguồn , điện &aacute;p ở đầu ra được khuyếch đại theo tỷ số v&ograve;ng d&acirc;y của biến &aacute;p. L&uacute;c n&agrave;y đầu ra l&agrave; chu kỳ dương .Ngược lại ở chu kỳ &acirc;m cuộn L2 của phần sơ cấp được cấp điện ,điện &aacute;p đầu ra l&agrave; chu kỳ &acirc;m của t&iacute;n hiệu.<br />\r\nDo đ&oacute; đầu ra của biến &aacute;p điện &aacute;p l&agrave; d&ograve;ng điện xoay chiều tần số 50Hz ,dạng s&oacute;ng vu&ocirc;ng.</p>\r\n\r\n<p><strong>Mạch điện thực tế :</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00050.jpg" style="height:562px; margin:0px; width:441px" /></p>', 'trick', 1, 'Việc tạo ra dòng điện là bước tiến vĩ đại trong lịch sử loài người .Để sản xuất ra dòng điện xoay chiều có rất nhiều phương pháp như thủy điện,nhiệt điện.'),
(4, 'Mạch nguồn 5V có mức ổn định tốt với IC 7805', '<p><strong>Ch&uacute;ng ta biết tr&ecirc;n thị trường lu&ocirc;n c&oacute; b&aacute;n c&aacute;c ic ổn &aacute;p 3 ch&acirc;n họ 78xx, họ 79xx. Vậy nếu muốn c&oacute; mức &aacute;p DC ngả ra ổn định, Bạn t&igrave;m v&agrave; d&ugrave;ng c&aacute;c ic ổn &aacute;p n&agrave;y.</strong></p>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px">&nbsp;</div>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px"><img alt="Mạch nguồn 5V có mức ổn định tốt với IC 7805" src="http://hc.serveftp.com/uploads/dien-tu/2013_08/machdien00002.jpg" style="height:215px; margin:0px; width:450px" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px">Ch&uacute;ng ta&nbsp;biết tr&ecirc;n thị trường lu&ocirc;n c&oacute; b&aacute;n c&aacute;c ic ổn &aacute;p 3 ch&acirc;n họ 78xx,&nbsp;họ 79xx. Vậy nếu muốn c&oacute; mức &aacute;p DC ngả ra ổn định, Bạn t&igrave;m&nbsp;v&agrave; d&ugrave;ng c&aacute;c ic ổn &aacute;p n&agrave;y. Với ic ổn &aacute;p 7805, mức &aacute;p ra l&agrave;&nbsp;5V, với ic ổn &aacute;p 7809, mức &aacute;p ra sẽ l&agrave; 9V, với ic ổn &aacute;p 7812,&nbsp;<br />\r\nmức &aacute;p ra sẽ l&agrave; 12V...IC ổn &aacute;p họ 79xx d&ugrave;ng tạo ổn &aacute;p tr&ecirc;n&nbsp;đường nguồn volt &acirc;m.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px">&nbsp; &nbsp;Trong&nbsp;mạch, ch&uacute;ng ta d&ugrave;ng tụ h&oacute;a lớn C1 tạo ổn &aacute;p tr&ecirc;n đường nguồn&nbsp;DC, đ&acirc;y l&agrave; dạng ổn &aacute;p thụ động, ch&uacute;ng ta d&ugrave;ng ic ổn &aacute;p 7805&nbsp;để c&oacute; mức &aacute;p ra 5V c&oacute; độ ổn định rất tốt, đ&acirc;y l&agrave; dạng ổn&nbsp;&aacute;p t&iacute;ch cực. Khi d&ugrave;ng ic ổn &aacute;p họ 78xx, họ 79xx, tr&ecirc;n ngả ra.&nbsp;<br />\r\n&nbsp; Bạn nhớ gắn th&ecirc;m tụ h&oacute;a d&ugrave;ng để tr&aacute;nh hiện tượng ph&aacute;t sinh&nbsp;dao động tự k&iacute;ch, khi mạch ổn &aacute;p trong ic bị dao động, Bạn sẽ&nbsp;thấy mức &aacute;p DC tr&ecirc;n ngả ra chập chờn l&uacute;c l&ecirc;n l&uacute;c xuống. Trường&nbsp;hợp đường nguồn 5V n&agrave;y d&ugrave;ng cấp điện cho c&aacute;c mạch điện l&agrave;m&nbsp;việc ở v&ugrave;ng tần số cao, l&uacute;c đ&oacute; Bạn phải gắn th&ecirc;m tụ nhỏ C3,&nbsp;<br />\r\nc&ocirc;ng dụng của c&aacute;c tụ nhỏ l&agrave; lọc bỏ c&aacute;c t&iacute;n hiệu tần số cao&nbsp;rất tốt, trong khi đ&oacute; do cấu tr&uacute;c b&ecirc;n trong của c&aacute;c tụ h&oacute;a&nbsp;lớn c&oacute; tiềm ẩn t&iacute;nh ống d&acirc;y, cuộn cảm n&ecirc;n kh&ocirc;ng lọc tốt c&aacute;c&nbsp;d&ograve;ng điện t&iacute;n hiệu tần số cao nhiễm tr&ecirc;n đường nguồn. Trong&nbsp;mạch ch&uacute;ng ta cũng d&ugrave;ng Led đỏ, Led xanh để l&agrave;m Led chỉ thị.</div>', 'trick', 1, 'Chúng ta biết trên thị trường luôn có bán các ic ổn áp 3 chân họ 78xx, họ 79xx. Vậy nếu muốn có mức áp DC ngả ra ổn định, Bạn tìm và dùng các ic ổn áp này.'),
(5, 'Mạch inverter 500W dùng transistor đơn giản', '<p><strong>Mạch nghịch lưu d&ugrave;ng 4047 Haicong.vn đ&atilde; lắp nhiều rồi , thường được ưu điểm xung ra ổn định nhưng nhược điểm l&agrave; ic kh&ocirc;ng chịu được d&ograve;ng cao hay ch&aacute;y.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch inverter 500W dùng transistor đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui3.jpg" style="height:181px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;Ngược lại với th&igrave; tranistor tỏ ra kh&aacute; ổn định ,dễ lắp , đặc biệt&nbsp;Haicong.vn đ&atilde; test thử 2 mạch tr&ecirc;n 1 biến &aacute;p th&igrave; c&ocirc;ng suất của tran lớn hơn . Đ&acirc;y l&agrave; mạch nghịch lưu 500W . lưu &yacute; : biến &aacute;p sơ cấp quấn d&acirc;y 3mm &aacute;p t&acirc;m 11 V , biến trở 100K để chỉnh đ&uacute;ng tần số 50Hz , tran Cs D718X5 mỗi vế , th&ecirc;m mạch lọc LC .Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</p>\r\n</div>\r\n</div>', 'trick', 0, 'Mạch nghịch lưu dùng 4047 Haicong.vn đã lắp nhiều rồi , thường được ưu điểm xung ra ổn định nhưng nhược điểm là ic không chịu được dòng cao hay cháy.'),
(6, 'Tổng quát về kỹ thuật truyền hình mầu', '<p><strong>Truyền h&igrave;nh mầu ra đời khi truyền h&igrave;nh đen trắng đ&atilde; trưởng th&agrave;nh v&agrave; vẫn c&ograve;n đang được sử dụng rộng d&atilde;i, v&igrave; vậy khi x&acirc;y dựng truyền h&igrave;nh mầu th&igrave; to&agrave;n bộ hệ thống truyền h&igrave;nh đen trắng vẫn được giữa nguy&ecirc;n v&agrave; người ta chỉ truyền th&ecirc;m c&aacute;c t&iacute;n hiệu mầu.</strong></p>\r\n\r\n<p>C&aacute;c t&iacute;n hiệu FM tiếng, t&iacute;n hiệu đồng bộ d&ograve;ng v&agrave; đồng bộ m&agrave;nh kh&ocirc;ng thay đổi, ri&ecirc;ng t&iacute;n hiệu Video nay được đổi th&agrave;nh hai t&iacute;n hiệu l&agrave; Y v&agrave; C, Y l&agrave; t&iacute;n hiệu ch&oacute;i mang th&ocirc;ng tin về h&igrave;nh ảnh đen trắng v&agrave; C l&agrave; s&oacute;ng mang phụ mang th&ocirc;ng tin về t&iacute;n hiệu mầu :</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00001.jpg" style="height:286px; margin:0px; width:450px" /></p>\r\n\r\n<p>Tổng qu&aacute;t về kỹ thuật truyền h&igrave;nh mầu .</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Như vậy trong t&iacute;n hiệu truyền h&igrave;nh mầu th&igrave; t&iacute;n hiệu Video tổng hợp bao gồm :</p>\r\n\r\n<p>+ T&iacute;n hiệu ch&oacute;i k&yacute; hiệu l&agrave; Y - mang th&ocirc;ng tin về h&igrave;nh ảnh đen trắng, đ&acirc;y ch&iacute;nh l&agrave; t&iacute;n hiệu Video được giữ lại khi ph&aacute;t triển truyền h&igrave;nh mầu, nhằm tương th&iacute;ch với c&aacute;c m&aacute;y thu h&igrave;nh đen trắng.</p>\r\n\r\n<p>+ T&iacute;n hiệu C l&agrave; s&oacute;ng mang phụ, mang th&ocirc;ng tin về mầu sắc</p>\r\n\r\n<p>+ T&iacute;n hiệu FM l&agrave; s&oacute;ng mang điều tần của t&iacute;n hiệu tiếng</p>\r\n\r\n<p>+ Xung H.syn l&agrave; xung đồng bộ d&ograve;ng</p>\r\n\r\n<p>+ Xung V.syn l&agrave; xung đồng bộ m&agrave;nh.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; So với truyền h&igrave;nh đen trắng th&igrave; t&iacute;n hiệu Y l&agrave; t&iacute;n hiệu thị tần, xung H.syn, xung V.syn, v&agrave; t&iacute;n hiệu FM l&agrave; kh&ocirc;ng thay đổi, như vậy truyền h&igrave;nh mầu thực chất l&agrave; truyền h&igrave;nh đen trắng c&oacute; th&ecirc;m t&iacute;n hiệu s&oacute;ng mang mầu C , điều n&agrave;y c&oacute; nghĩa l&agrave; tất cả c&aacute;c kiến thức về truyền h&igrave;nh đen trắng đều được tận dụng lại, v&igrave; vậy hiểu được truyền h&igrave;nh đen trắng sẽ gi&uacute;p bạn hiểu truyền h&igrave;nh mầu dễ d&agrave;ng hơn.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00002.gif" style="height:190px; margin:0px; width:312px" /></p>\r\n\r\n<p>Phổ t&iacute;n hiệu Video tổng hợp</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; T&iacute;n hiệu C ( s&oacute;ng mang mầu ) được tạo ra bằng c&aacute;ch n&agrave;o ? t&iacute;n hiệu mầu R, G, B&nbsp; l&agrave; g&igrave; ? c&aacute;c c&acirc;u hỏi đ&oacute; sẽ được giải đ&aacute;p sau khi ta t&igrave;m hiểu về bản chất &aacute;nh s&aacute;ng v&agrave; mầu sắc cũng như một số đặc điểm của mắt người.</p>\r\n\r\n<p><strong>&nbsp;2/ T&iacute;nh chất của &aacute;nh s&aacute;ng v&agrave; mầu sắc</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Mục đ&iacute;ch của b&agrave;i n&agrave;y l&agrave; để t&igrave;m hiểu nguồn gốc của t&iacute;n hiệu C ( Choma_) l&agrave; s&oacute;ng mang phụ của t&iacute;n hiệu mầu, v&agrave; t&igrave;m hiểu c&aacute;c t&iacute;n hiệu mầu.</p>\r\n\r\n<p><em><strong>&nbsp;&nbsp;&nbsp;&nbsp; 2.1 - Bản chất kh&aacute;ch quan của &aacute;nh s&aacute;ng</strong></em></p>\r\n\r\n<p>&nbsp;&nbsp; &Aacute;nh s&aacute;ng về phương diện vật l&yacute; kh&aacute;ch quan chỉ l&agrave; s&oacute;ng điện từ c&oacute; tần số từ 3,8 x 1014&nbsp;đến 7,8 x 1014&nbsp; Hz, hay c&oacute; bước s&oacute;ng từ 380nm đến 780nm như vậy &aacute;nh s&aacute;ng chỉ chiếm một khoảng rất hẹp trong dải s&oacute;ng điện từ, dải đ&oacute; ta gọi l&agrave; phổ &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><em><strong>&nbsp;&nbsp; 2.2 Cảm nhận chủ quan của mắt người</strong></em></p>\r\n\r\n<p>&nbsp; M&agrave;u sắc ho&agrave;n to&agrave;n l&agrave; cảm gi&aacute;c chủ quan của con người</p>\r\n\r\n<p>&nbsp;Trong to&agrave;n bộ phổ &aacute;nh s&aacute;ng từ 380nm đến 780nm sẽ cho cảm gi&aacute;c ở mắt người l&agrave; nguồn s&aacute;ng trắng, thực tế phổ &aacute;nh s&aacute;ng n&agrave;y l&agrave; t&acirc;p hợp của rất nhiều &aacute;nh s&aacute;ng đơn sắc.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00003.gif" style="height:59px; margin:0px; width:393px" /></p>\r\n\r\n<p>Mầu phổ</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp; Mầu của vật</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Thực ra một vật ( kh&ocirc;ng phải l&agrave; nguồn s&aacute;ng ) th&igrave; kh&ocirc;ng c&oacute; mầu, v&iacute; dụ một vật bất kỳ nếu ta kh&ocirc;ng chiếu &aacute;nh s&aacute;ng v&agrave;o n&oacute; th&igrave; ta kh&ocirc;ng nh&igrave;n thấy vật đ&oacute;.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<strong>Th&iacute; nghiệm :</strong>&nbsp;Trong ph&ograve;ng tối, nếu ta chiếu &aacute;nh s&aacute;ng trắng v&agrave;o tờ giấy th&igrave; ta thấy tờ giấy mầu trắng, nếu ta chiếu &aacute;nh s&aacute;ng đơn sắc mầu đỏ v&agrave;o tờ giấy ta lại thấy tờ giấy mầu đỏ, chiếu &aacute;nh s&aacute;ng xanh ta lại thấy tờ giấy mầu xanh =&gt; Chứng tỏ mầu của vật chỉ đ&uacute;ng khi c&oacute; một nguồn s&aacute;ng trắng chiếu v&agrave;o.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Một nguồn s&aacute;ng trắng l&agrave; tập hợp của v&ocirc; số nguồn s&aacute;ng đơn sắc, khi chiếu v&agrave;o một vật n&agrave;o đ&oacute; th&igrave; một số bước s&oacute;ng bị vật đ&oacute; hấp thụ ho&agrave;n to&agrave;n hay một phần, phần c&ograve;n lại phản chiếu đến mắt cho ta cảm gi&aacute;c về một mầu n&agrave;o đ&oacute;.</p>\r\n\r\n<p><strong>&nbsp;&nbsp; C&aacute;c đặc t&iacute;nh x&aacute;c định một mầu<br />\r\n&nbsp;&nbsp;&nbsp;</strong>Một mầu được x&aacute;c định dựa tr&ecirc;n 3 yếu tố l&agrave; :</p>\r\n\r\n<p><strong>Sắc th&aacute;i của mầu</strong>&nbsp;: yếu tố n&agrave;y để ph&acirc;n biệt c&aacute;c mầu sắc kh&aacute;c nhau .</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00004.gif" style="height:117px; margin:0px; width:257px" /></p>\r\n\r\n<p>Hai mầu c&oacute; sắc th&aacute;i kh&aacute;c nhau c&oacute; phổ kh&aacute;c nhau</p>\r\n\r\n<p><strong>Độ ch&oacute;i của mầu</strong>&nbsp;: độ ch&oacute;i mầu l&agrave; cường độ s&aacute;ng của mầu đ&oacute; mạnh hay yếu.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00005.gif" style="height:164px; margin:0px; width:169px" /></p>\r\n\r\n<p>Hai mầu c&ugrave;ng sắc th&aacute;i nhưng c&oacute; độ ch&oacute;i kh&aacute;c nhau<br />\r\ncho ta cường độ s&aacute;ng kh&aacute;c nhau</p>\r\n\r\n<p><strong>Độ b&atilde;o ho&agrave; mầu</strong>&nbsp;: Chỉ độ tinh khiết của mầu so với mầu trắng, mầu trắng c&oacute; độ b&atilde;o ho&agrave; mầu bằng 0.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00006.gif" style="height:141px; margin:0px; width:257px" /></p>\r\n\r\n<p>Hai mầu c&ugrave;ng sắc th&aacute;i nhưng c&oacute; độ b&atilde;o ho&agrave; mầu kh&aacute;c nhau,<br />\r\n&nbsp;mầu c&oacute; độ b&atilde;o ho&agrave; c&agrave;ng cao th&igrave; phổ c&agrave;ng hẹp</p>\r\n\r\n<p><em>&nbsp;<strong>&nbsp;2.3 Cấu tạo của mắt người</strong></em></p>\r\n\r\n<p>&nbsp; Bằng c&aacute;c nghi&ecirc;n cứu về cấu tạo của mắt người, c&aacute;c nh&agrave; khoa học đ&atilde; nhận thấy rằng, mắt người k&eacute;m nhậy với c&aacute;c mầu đơn sắc, khi c&oacute; một nguồn s&aacute;ng l&agrave; tập hợp của nhiều mầu đơn sắc th&igrave; mắt ngư&ograve;i kh&ocirc;ng thể ph&acirc;n biệt được c&aacute;c mầu đơn sắc đ&oacute; m&agrave; c&aacute;c tế b&agrave;o thần kinh của mắt cho ta cảm gi&aacute;c về một mầu kh&aacute;c<br />\r\n&nbsp;&nbsp; Th&iacute; dụ : nếu chiếu một ch&ugrave;m s&aacute;ng c&oacute; đầy đủ c&aacute;c mầu đơn sắc gồm Đỏ, cam, v&agrave;ng, lục , lam , lơ , t&iacute;m&nbsp; v&agrave;o mắt th&igrave; ta c&oacute; cảm nhận đ&oacute; l&agrave; mầu trắng,&nbsp; nếu chiếu hai mầu đơn sắc l&agrave; đỏ v&agrave; xanh l&aacute; th&igrave; ta cảm nhận đ&oacute; l&agrave; mầu v&agrave;ng.</p>\r\n\r\n<p><strong><em>&nbsp; 2.4&nbsp;&nbsp;&nbsp; Ba mầu sắc cơ bản trong tự nhi&ecirc;n.&nbsp;</em></strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Bằng c&aacute;c thực nghiệm người ta chứng minh được rằng, trong phổ &aacute;nh s&aacute;ng c&oacute; ba mầu đơn sắc c&oacute; đặc điểm , từ ba mầu đ&oacute; c&oacute; thể tổng hợp th&agrave;nh một mầu bất kỳ ( mầu bất kỳ l&agrave; cảm nhận của mắt ) v&agrave; ngược lại một mầu bất kỳ ta cũng c&oacute; thể ph&acirc;n t&iacute;ch th&agrave;nh ba mầu cơ bản đ&oacute; , ba mầu cơ bản đ&oacute; l&agrave; .</p>\r\n\r\n<p>+ Mầu đỏ&nbsp; : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; R ( Red )</p>\r\n\r\n<p>+ Mầu xanh l&aacute; : G ( Green )</p>\r\n\r\n<p>+ Mầu xanh lơ : B ( Blue )</p>\r\n\r\n<p>&nbsp; Ba mầu sắc n&agrave;y sẽ được ứng dụng trong kỹ thuật truyền h&igrave;nh v&agrave; trong c&aacute;c thiết bị c&oacute; h&igrave;nh mầu như m&aacute;y in mầu, điện thoại di động, m&aacute;y vi t&iacute;nh v v...</p>\r\n\r\n<p><em><strong>&nbsp; 2.5&nbsp; Nguy&ecirc;n l&yacute; trộn mầu</strong></em></p>\r\n\r\n<p><strong>&nbsp; Th&iacute; nghiệm&nbsp;</strong>:&nbsp; C&oacute; ba nguồn s&aacute;ng đơn sắc ph&aacute;t ra ba mầu : Đỏ, Xanh l&aacute;, Xanh lơ&nbsp; c&ugrave;ng chiếu l&ecirc;n một ph&ocirc;ng mầu trắng, ta h&atilde;y quan s&aacute;t mầu sắc tại c&aacute;c vị tr&iacute; m&agrave; c&aacute;c mầu giao nhau :</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00007.gif" style="height:218px; margin:0px; width:395px" /></p>\r\n\r\n<p>Nguy&ecirc;n l&yacute; trộn mầu từ ba mầu cơ bản R , G , B</p>\r\n\r\n<p>+ Ba nguồn s&aacute;ng tr&ecirc;n c&oacute; cường độ bằng nhau v&agrave; bằng 100%<br />\r\n&nbsp;&nbsp; R = G = B = 100%</p>\r\n\r\n<p>+ R + G =&nbsp; V&agrave;ng&nbsp;&nbsp; ( Đỏ + Xanh l&aacute; = Mầu v&agrave;ng )</p>\r\n\r\n<p>+ R + G + B = Trắng ( Đỏ + Xanh l&aacute; + Xanh lơ = Trắng )</p>\r\n\r\n<p>+ R + B = T&iacute;m ( Đỏ + Xanh lơ = T&iacute;m )</p>\r\n\r\n<p>+ G + B = Xanh dương ( Xanh l&aacute; + Xanh lơ = Xanh dương )</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00008.gif" style="height:203px; margin:0px; width:232px" /></p>\r\n\r\n<p>Trộn từ 3 mầu cơ bản R, G , B&nbsp; với c&aacute;c<br />\r\ncường độ s&aacute;ng kh&aacute;c nhau</p>', 'trick', 0, 'Truyền hình mầu ra đời khi truyền hình đen trắng đã trưởng thành và vẫn còn đang được sử dụng rộng dãi, vì vậy khi xây dựng truyền hình mầu thì toàn bộ hệ thống truyền hình đen trắng vẫn được giữa nguyên và người ta chỉ truyền thêm các tín hiệu mầu.'),
(7, 'Khái niệm về từ trường', '<p><strong>1. Kh&aacute;i niệm về từ trường.&nbsp;</strong><br />\r\n<strong>&nbsp; Nam ch&acirc;m v&agrave; từ t&iacute;nh .</strong><br />\r\nTrong tự nhi&ecirc;n c&oacute; một số chất c&oacute; thể h&uacute;t được sắt gọi l&agrave; nam ch&acirc;m tự nhi&ecirc;n.<br />\r\nTrong c&ocirc;ng nghiệm người ta luyện th&eacute;p hoặc hợp chất th&eacute;p để tạo th&agrave;nh nam ch&acirc;m nh&acirc;n tạo.<br />\r\nNam ch&acirc;m lu&ocirc;n lu&ocirc;n c&oacute; hai cực l&agrave; cực bắc North (N) v&agrave; cực nam South (S) , nếu chặt thanh nam ch&acirc;m ra l&agrave;m 2 th&igrave; ta lại được hai nam ch&acirc;m mới cũng c&oacute; hai cực N v&agrave; S &ndash; đ&oacute; l&agrave; nam ch&acirc;m c&oacute; t&iacute;nh chất kh&ocirc;ng ph&acirc;n chia..<br />\r\nNam ch&acirc;m thường được ứng dụng để sản xuất loa điện động, micro hoặc m&ocirc; tơ DC.<br />\r\n<br />\r\n<strong>&nbsp; Từ trường&nbsp;</strong><br />\r\nTừ trường l&agrave; v&ugrave;ng kh&ocirc;ng gian xung quanh nam ch&acirc;m c&oacute; t&iacute;nh chất truyền lực từ l&ecirc;n c&aacute;c vật liệu c&oacute; từ t&iacute;nh, từ trường l&agrave; tập hợp của c&aacute;c đường sức đi từ Bắc đến cực nam.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/namcham1.gif" style="height:124px; margin:0px; width:232px" /></div>\r\n\r\n<p><strong>&nbsp; Cường độ từ trường&nbsp;</strong><br />\r\nL&agrave; đại lượng đặc trưng cho độ mạnh yếu của từ trường, k&yacute; hiệu l&agrave; H đơn vị l&agrave; A/m</p>\r\n\r\n<p><strong>&nbsp; Độ từ cảm</strong><br />\r\nL&agrave; đại lượng đặc trưng cho vật c&oacute; từ t&iacute;nh chịu t&aacute;c động của từ trường, độ từ cảm phụ thuộc v&agrave;o vật liệu . VD&nbsp; Sắt c&oacute; độ từ cảm mạnh hơn đồng nhiều lần . Độ từ cảm được t&iacute;nh bởi c&ocirc;ng thức</p>\r\n\r\n<p>B = &micro;.H</p>\r\n\r\n<p>Trong đ&oacute; B : l&agrave;&nbsp; độ từ cảm<br />\r\n&micro; : l&agrave; độ từ thẩm<br />\r\nH :&nbsp; l&agrave; cường độ từ trường</p>\r\n\r\n<p><strong>&nbsp; Từ th&ocirc;ng</strong><br />\r\nL&agrave; số đường sức đi qua một đơn vị diện t&iacute;ch, từ th&ocirc;ng tỷ lệ thuật với cường độ từ trường.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/tuthong.png" style="height:131px; margin:0px; width:232px" /></div>\r\n\r\n<p><strong>&nbsp; Ứng dụng của Nam ch&acirc;m vĩnh cửu.</strong><br />\r\nNam ch&acirc;m vĩnh cửu được ứng dụng nhiều trong thiết bị điện tử, ch&uacute;ng được d&ugrave;ng để sản xuất Loa, Micro v&agrave; c&aacute;c loại M&ocirc; tơ DC.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/speaker.png" style="height:120px; margin:0px; width:120px" /></div>', 'trick', 0, 'Các chủ đề nghiên cứu : Nam châm và từ tính, từ trường, cường độ từ trường,độ từ cảm, từ thông.'),
(8, 'Mạch PCB chỉnh Bass, Treble, Volume, Blance dùng IC', '<p><strong>TDA1524A, v&agrave; phần mạch khuếch đại c&ocirc;ng suất d&ugrave;ng TDA LA 4440 với P=25w nghe cực k&igrave; ấm v&agrave; đ&atilde; ko bị &ugrave; r&egrave; ,với phần thiết kế đ&atilde; ghi r&otilde; chi tiết từng con linh kiện d&acirc;n diện tử ai m&ecirc; nhạc th&igrave; c&oacute; thể độ chế ri&ecirc;ng cho m&igrave;nh .</strong></p>\r\n\r\n<p><strong>Th&ocirc;ng số của mạch :</strong><br />\r\n+ Điện &aacute;p đầu v&agrave;o : 12VDC<br />\r\n+ T&iacute;n hiệu đầu v&agrave;o : L, R<br />\r\n+ T&iacute;n hiệu đầu ra : L, R</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">+ N&uacute;t điều chỉnh : Bass, Treble, Volume, Blance<img alt="Mạch PCB chỉnh Bass, Treble, Volume, Blance dùng IC" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/chuyendoi0012.jpg" style="height:343px; margin:0px; width:450px" /></div>\r\n\r\n<p><img alt="Mạch PCB chỉnh Bass, Treble, Volume, Blance dùng IC" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/mach0009.jpg" style="height:347px; margin:0px; width:450px" /></p>', 'trick', 0, 'TDA1524A, và phần mạch khuếch đại công suất dùng TDA LA 4440 với P=25w nghe cực kì ấm và đã ko bị ù rè ,với phần thiết kế đã ghi rõ chi tiết từng con linh kiện dân diện tử ai mê nhạc thì có thể độ chế riêng cho mình.'),
(9, 'Hướng dẫn dùng IC 320WC', '<p><strong>Mạch được tạo bởi 2 con IC 555 d&ugrave;ng để tạo dao động. Tần số được điều khiển bởi ch&acirc;n 5 của IC. Đầu ti&ecirc;n l&agrave; IC 1 được l&agrave;m việc xung quang tần số l&agrave; 1hz v&agrave; tụ 47uF được nạp điện v&agrave; sau đ&oacute; l&agrave; xả điện li&ecirc;n tục qu&aacute; tr&igrave;nh đ&oacute; cứ diễn ra li&ecirc;n tục như vậy.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao1.jpg" style="height:226px; margin:0px; width:223px" /></p>\r\n\r\n<p><strong>Đ&acirc;y l&agrave; mạch tạo &acirc;m thanh đơn giản d&ugrave;ng cho c&ograve;i b&aacute;o động cho cảnh s&aacute;t</strong></p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao2.jpg" style="height:207px; margin:0px; width:450px" /></p>\r\n\r\n<p>Mạch được tạo bởi 2 con IC 555 d&ugrave;ng để tạo dao động. Tần số được điều khiển bởi ch&acirc;n 5 của IC. Đầu ti&ecirc;n l&agrave; IC 1 được l&agrave;m việc xung quang tần số l&agrave; 1hz v&agrave; tụ 47uF được nạp điện v&agrave; sau đ&oacute; l&agrave; xả điện li&ecirc;n tục qu&aacute; tr&igrave;nh đ&oacute; cứ diễn ra li&ecirc;n tục như vậy. Tần số ra loa được điều chế bởi IC2 v&agrave; ta nghe được &acirc;m thanh ra loa.<br />\r\nHai biến trở VR1 v&agrave; VR2 d&ugrave;ng để điều chế tần số đầu ra cho Loa. Tần số cảng lớn th&igrave; tiếng h&uacute; c&ograve;i c&agrave;ng to. Đ&acirc;y l&agrave; mạch b&aacute;o động &aacute;nh s&aacute;ng. Được sử dụng trong ph&ograve;ng tối v&agrave; khi c&oacute; &aacute;nh s&aacute;ng lọt v&agrave;o th&igrave; mạch sẽ ph&aacute;t ra tiếng b&aacute;o cho ta biết l&agrave; c&oacute; &aacute;nh s&aacute;ng</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao3.jpg" style="height:216px; margin:0px; width:450px" /></p>\r\n\r\n<p>Nh&igrave;n v&agrave;o mạch tr&ecirc;n kh&aacute; l&agrave; đơn giản v&igrave; mạch chỉ sử dụng 1 con 555 để tạo dao động ph&aacute;t &acirc;m thanh ra loa v&agrave; 1 con LDR cảm biến &aacute;nh s&aacute;ng.<br />\r\n+ 555 ở đ&acirc;y l&agrave; con tạo dao động xung vu&ocirc;ng trong mạch n&agrave;y n&oacute; tạo dao động l&agrave; 1Khz cấp cho tải l&agrave; Loa<br />\r\n+ LDR l&agrave; cảm biến &aacute;nh s&aacute;ng. Khi kh&ocirc;ng c&oacute; anh s&aacute;ng th&igrave; cảm biến n&agrave;y c&oacute; gi&aacute; trị điện trở l&agrave; v&ocirc; c&ugrave;ng c&ograve;n khi c&oacute; &aacute;nh s&aacute;ng đủ mạch th&igrave; cảm biến c&oacute; gi&aacute; trị điện trở l&agrave; 0.<br />\r\nKhi c&oacute; &aacute;nh s&aacute;ng th&igrave; LDR sẽ c&oacute; điện trở bằng 0 khi đ&oacute; n&oacute; sẽ ph&acirc;n cực thuận cho con BC158 dẫn đến cấp điện &aacute;p v&agrave;o ch&acirc;n 4 của 555 l&agrave; mạch dao động 555 hoạt động v&agrave; ph&aacute;t &acirc;m thanh ra loa. C&ograve;n khi kh&ocirc;ng c&oacute; &aacute;nh s&aacute;ng th&igrave; LDR c&oacute; gi&aacute; trị điện trở v&ocirc; c&ugrave;ng do đ&oacute; n&oacute; ko ph&acirc;n cực được cho BC158 &gt; Kh&ocirc;ng c&oacute; t&iacute;n hiệu ra loa. Biến trở 100K d&ugrave;ng để điều chỉnh mức cường độ &aacute;nh s&aacute;ng cảnh b&aacute;o. Đ&acirc;y l&agrave; mạch b&aacute;o trộm nh&agrave; 4 trong 1. Sử dụng NE556 l&agrave;m cảnh b&aacute;o kết hợp với chu&ocirc;ng b&aacute;o động,</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao4.jpg" style="height:248px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Mạch n&agrave;y được hoạt động khi phải thỏa m&atilde;n 4 điều kiện kh&aacute;c sau đ&acirc;y :</strong><br />\r\n+ Khi đ&egrave;n s&aacute;ng th&igrave; cảm biến LDR1 được bật<br />\r\n+ Khi đ&egrave;n s&aacute;ng th&igrave; cảm biến LDR2 được bật<br />\r\n+ Khi mở cửa<br />\r\n+ Khi chạm v&agrave;o vật (H&igrave;nh vẽ)<br />\r\nNếu m&agrave; kẻ trộm bị gặp phải 1 trong 4 trường hợp tr&ecirc;n th&igrave; sẽ c&oacute; 1 tiếng chu&ocirc;ng b&aacute;o động cho người bit l&agrave; c&oacute; kẻ trộmĐ&acirc;y l&agrave; mạch kh&ocirc;ng chỉ cho ch&uacute;ng ta biết mức nước ở thời điểm hiện tại m&agrave; n&oacute; c&ograve;n cảnh b&aacute;o cho ch&uacute;ng ta khi mức nước đ&atilde; đầy bằng hiện thị l&ecirc;n c&aacute;c đ&egrave;n LED v&agrave; chu&ocirc;ng b&aacute;o động</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao5.jpg" style="height:285px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Mạch sử dụng IC CMOS CD4066</strong>&nbsp;để đ&oacute;ng cắt v&agrave; đưa t&iacute;n hiệu mức nước ra c&aacute;c LED (Tr&ecirc;n h&igrave;nh vẽ). Khi mức nước trong th&ugrave;ng kh&ocirc;ng c&oacute; nước th&igrave; điện trở 180K &ocirc;m được nối với mức thấp (GND) n&ecirc;n kh&ocirc;ng c&oacute; đ&egrave;n LED n&agrave;o s&aacute;ng. Khi c&oacute; mức nước v&agrave;o th&igrave; đầu ti&ecirc;n n&oacute; kết nối giữa S1 với nguồn dương th&ocirc;ng qua c&aacute;c ion trong nước khi đ&oacute; đ&oacute;ng kh&oacute;a S1 tạo cho đ&egrave;n LED 1 s&aacute;ng. Cứ như vậy th&igrave; c&aacute;c mức 2, 3, ,4 cũng như vậy mức nước đến đ&acirc;u th&igrave; đ&egrave;n sẽ s&aacute;ng đến đ&oacute;.<br />\r\nRi&ecirc;ng khi mức nước đ&atilde; đầy c&oacute; th&ecirc;m 1 chu&ocirc;ng cảnh b&aacute;o l&agrave; nước trong th&ugrave;ng đ&atilde; đầy. b&aacute;o hiệu cho con người l&agrave; nước đ&atilde; đầy th&ugrave;ng.</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao6.jpg" style="height:295px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Đ&acirc;y l&agrave; mạch cảnh b&aacute;o mức nước</strong>&nbsp;đ&atilde; đầy hay b&aacute;o động cho 1 mức nước n&agrave;o đ&oacute;. Mạch sử dụng con 555 tạo dao động ph&aacute;t &acirc;m thanh ra loa. Tần số ra loa để b&aacute;o động sẽ l&agrave; tần số dao động của IC555 : Tần số l&agrave; : f = 1/(0.693*0.022.10^-6*(100.10^3+9.4.10^3) = ?. Tần số n&agrave;y kh&ocirc;ng được lớn hơn 20Khz v&igrave; như thế l&agrave;m sao m&agrave; tai người c&oacute; thể nghe thấy được.<br />\r\nHoạt động : Mạch kh&aacute; l&agrave; đơn giản gồm 1 con BC109C v&agrave; bộ cảm biến cũng kh&aacute; đơn giản l&agrave; cảm biến nước. Khi mức nước d&acirc;ng cao đến chỗ cảm biến nước th&igrave; l&uacute;c đ&oacute; d&ograve;ng điện từ nguồn được dẫn qua ion nước ph&acirc;n cực cho B của Transitor. Khi đ&oacute; transitor dẫn d&ograve;ng từ nguồn cấp nguồn cho 555. Dẫn đến 555 tạo dao động ph&aacute;t &acirc;m thanh ra loa. Khi mức nước kh&ocirc;ng chạm cảm biến th&igrave; B kh&ocirc;ng được ph&acirc;n cực ngắt nguồn cấp cho 555 n&ecirc;n loa sẽ kh&ocirc;ng ph&aacute;t t&iacute;n hiệu&nbsp;</p>\r\n</div>\r\n</div>', 'IC', 1, 'Mạch được tạo bởi 2 con IC 555 dùng để tạo dao động. Tần số được điều khiển bởi chân 5 của IC. Đầu tiên là IC 1 được làm việc xung quang tần số là 1hz và tụ 47uF được nạp điện và sau đó là xả điện liên tục quá trình đó cứ diễn ra liên tục như vậy.'),
(10, 'Cách lắp IC nối tiếp theo chuẩn RC32', '<p><strong>Mạch nghịch lưu d&ugrave;ng 4047 Haicong.vn đ&atilde; lắp nhiều rồi , thường được ưu điểm xung ra ổn định nhưng nhược điểm l&agrave; ic kh&ocirc;ng chịu được d&ograve;ng cao hay ch&aacute;y.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch inverter 500W dùng transistor đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui3.jpg" style="height:181px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;Ngược lại với th&igrave; tranistor tỏ ra kh&aacute; ổn định ,dễ lắp , đặc biệt&nbsp;Haicong.vn đ&atilde; test thử 2 mạch tr&ecirc;n 1 biến &aacute;p th&igrave; c&ocirc;ng suất của tran lớn hơn . Đ&acirc;y l&agrave; mạch nghịch lưu 500W . lưu &yacute; : biến &aacute;p sơ cấp quấn d&acirc;y 3mm &aacute;p t&acirc;m 11 V , biến trở 100K để chỉnh đ&uacute;ng tần số 50Hz , tran Cs D718X5 mỗi vế , th&ecirc;m mạch lọc LC .Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</p>\r\n</div>\r\n</div>', 'IC', 1, 'Có nhiều chuẩn lắp IC nhưng chuẩn RC32 là một chuẩn khá khó. Nó đòi hỏi chúng ta phải có am hiểu sâu sắc về cấu trúc IC cũng như hoạt động của nó. Sau đây chúng tôi sẽ hướng dẫn các bạn cách lắp IC nối tiếp theo chuẩn RC32 để có thể tối ưu cho các mạch câu, mạch vòng và đăc biệt là mạch đa xung.');
INSERT INTO `tbl_documents` (`doc_id`, `doc_title`, `doc_content`, `doc_type`, `doc_vip`, `doc_summary`) VALUES
(11, 'Kinh nghiệm sử dụng IC lâu năm', '<p><strong>L&agrave; ngưới th&iacute;ch khoa điện tử chắc ai cũng phải l&agrave;m cho m&igrave;nh một mạch nguồn để thực h&agrave;nh ! T&ugrave;y c&aacute;ch l&agrave;m của từng người nhưng cơ bản th&igrave; vẫn sử dụng c&aacute;c IC ổn &aacute;p họ 78 &amp; 79 , kết hợp với Ic Lm317 Lm337 để ra điện &aacute;p biến đổi .</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image001_3.jpg" style="height:332px; margin:0px; width:450px" /></p>\r\n\r\n<p>Về nguy&ecirc;n tắc th&igrave; ch&uacute;ng ta sử dụng diot cầu kết hợp c&aacute;c tụ điện cho ra điện &aacute;p đối xứng cấp cho từng cặp ic 78,79 .Th&ecirc;m phần bảo vệ cầu ch&igrave; hoặc role ch&uacute;ng ta đ&atilde; c&oacute; một bộ nguồn ổn định để suw3 dụng .<img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image002_3.jpg" style="height:192px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image003_3.jpg" style="height:236px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image004_2.jpg" style="height:419px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image005_2.jpg" style="height:276px; margin:0px; width:450px" /></p>\r\n</div>\r\n</div>\r\n\r\n<h2>&nbsp;</h2>', 'IC', 1, 'IC là một linh kiện điện tử quan trọng. Tuy nhiên tuổi thọ của nó cũng không phải là quá lâu năm. Trong bài viết này chúng tôi sẽ hướng dẫn các bạn cách sử dụng IC được lâu hơn và bền hơn.'),
(12, 'IC bị ngấm nước - phải làm sao?', '<p><strong>H&acirc;n hạnh giới thiệu đến c&aacute;c bạn một mạch nạp c&oacute; khả năng tự động ngắt mạch khi b&igrave;nh đầy nhằm bảo vệ tốt nhất cho ắc quy .</strong></p>\r\n\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p>&nbsp;Tr&ecirc;n thực tế th&igrave; mạch nạp cho ắc quy rất đơn giản chỉ cần một con diot chỉnh lưu l&agrave; đủ , nhưng như thế rất hại cho b&igrave;nh khi đ&atilde; đầy điện m&agrave; vẫn nạp tiếp.</p>\r\n\r\n<p>Dựa tr&ecirc;n cơ sở so s&aacute;nh điện &aacute;p của ic khuếch đại thuật to&aacute;n đối chiếu với lưu lượng điện trong b&igrave;nh m&agrave; mạch dưới đ&acirc;y c&oacute; khả năng nạp tự động điều chỉnh lượng điện &aacute;p nạp cho b&igrave;nh .<img alt="Hướng dẩn lắp ráp mạch nạp Acquy tự động" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui1.jpg" style="height:162px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Nguy&ecirc;n l&yacute; hoạt động rất đơn giản như sau :</strong></p>\r\n\r\n<p>Diot zen tạo điện &aacute;p tham chiếu ở đ&acirc;y sử dụng dieenot zen 6V . Nhờ diot n&agrave;y m&agrave; điện &aacute;p tham chiếu v&agrave;o ch&acirc;n 2 lu&ocirc;n được giữ cố định d&ugrave; b&igrave;nh đ&atilde; vơi điện .<br />\r\nBiến trở v&agrave; c&aacute;c điện trở ph&acirc;n &aacute;p v&agrave;o ch&acirc;n số 3 lấy điện &aacute;p thực tế của b&igrave;nh so s&aacute;nh với điện &aacute;p tham chiếu ở ch&acirc;n số 2 . Khi điện &aacute;p của b&igrave;nh chưa đầy điện &aacute;p ở ch&acirc;n số 3 nhỏ hơn điện &aacute;p ch&acirc;n 2 đầu ra khuếch đại thuật to&aacute;n ở mức 0 , role chưa c&oacute; điện &aacute;p k&iacute;ch mở , d&ograve;ng điện được nạp ở b&igrave;nh nhờ điot cầu . Khi b&igrave;nh đầy ch&acirc;n ch&acirc;n số 3 điện &aacute;p lớn hơn điện &aacute;p so s&aacute;nh , l&uacute;c n&agrave;y ch&acirc;n 6 cấp d&ograve;ng k&iacute;ch mở tran đ&oacute;ng điện cho role , khi đ&oacute; sẽ c&aacute;ch ly b&igrave;nh với d&ograve;ng nạp , đ&egrave;n led b&aacute;o s&aacute;ng , khi đ&oacute; ta c&oacute; thể ngắt b&igrave;nh ra được ,</p>\r\n\r\n<p>Ch&uacute; &yacute; b&igrave;nh ắc quy đầy ở điện &aacute;p tầm 13,7V sử dụng biến trở điều chỉnh đ&uacute;ng điện &aacute;p sao cho role tự ngắt ở điện &aacute;p n&agrave;y ,<br />\r\nnếu k c&oacute; diot zen 6v th&igrave; d&ugrave;ng 3 v vẫn được nhưng cần ph&acirc;n &aacute;p lại<br />\r\nic ở đ&acirc;y l&agrave; Lm741 c&oacute; thể thay thế bằng c&aacute;c loại kh&aacute;c như Lm358, 324&hellip; cần x&aacute;c định đ&uacute;ng ch&acirc;n trước khi sử d&ugrave;ng.<br />\r\nD&ograve;ng nạp nhỏ hơn 1/10 dung lượng ắc quy sẽ k&eacute;o d&agrave;i tuổi thọ của b&igrave;nh , vd b&igrave;nh 25a th&igrave; biến &aacute;p chọn loại 3a l&agrave; hợp l&yacute;.<br />\r\nRole se k đ&oacute;ng ngay lập tức m&agrave; từ từ do đ&oacute; sẽ điều chỉnh được d&ograve;ng nạp hợp l&yacute; ta kh&ocirc;ng cần băn khoăn về điều n&agrave;y</p>\r\n\r\n<p><strong>Mạch in :</strong></p>\r\n\r\n<p><img alt="Hướng dẩn lắp ráp mạch nạp Acquy tự động" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui2.jpg" style="height:211px; margin:0px; width:450px" /></p>\r\n</div>', 'ic', 1, 'Vào một ngày đẹp trời, con IC tươi đẹp của chúng ta không may bị ngấm nước, lúc này nếu không biết cách khắc phục kịp thời, chúng ta có thể phải trả giá rất đắt.'),
(13, 'Cách nhận biết IC chất lượng cao', '<p><strong>Bản demo led cube 8x8x8 . thử tr&ecirc;n 89S52 , ch&uacute;ng t&ocirc;i khuy&ecirc;n c&aacute;c bạn kh&ocirc;ng n&ecirc;n l&agrave;m ledcube với loại vdk n&agrave;y , thứ nhất tr&igrave;nh bi&ecirc;n dich kh&ocirc;ng hỗ trợ mảng nhiều chiều tốt , đưa mảng [10][8] tr&igrave;nh keliC b&aacute;o lỗi tr&agrave;n bộ nhớ trong khi với AVR viết bao nhi&ecirc;u t&ugrave;y th&iacute;ch.</strong></p>\r\n\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p>Thứ 2 quan trọng nhất l&agrave; bộ nhớ với việc xuất 64 bit một l&uacute;c th&igrave; chắc tầm 5,6 hiệu ứng l&agrave; full rồi. Ở bản demo n&agrave;y&nbsp;chu1ng t&ocirc;i&nbsp;sử dụng 74hc595 qu&eacute;t cột v&agrave; uln2803 qu&eacute;t lớp , với 8x8x8 ta c&oacute; thể sử dụng th&agrave;nh một led matrix để chạy chữ,.Chế độ qu&eacute;t giống như matrix 8&times;8 tự update code khi thay đổi chuỗi k&yacute; tự (chi tiết xem video ph&iacute;a dưới).</p>\r\n\r\n<p>Phi&ecirc;n bản thử nghiệm tr&ecirc;n 89s52 . C&ograve;n đ&acirc;y l&agrave; một số h&igrave;nh ảnh về bản demo n&agrave;y :</p>\r\n\r\n<p><strong>Khối led :</strong></p>\r\n\r\n<p><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00160.jpg" style="height:360px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00161.jpg" style="height:338px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Sơ đồ nguy&ecirc;n l&yacute; :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00162.jpg" style="height:245px; margin:0px; width:450px" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mạch in :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00163.jpg" style="height:218px; margin:0px; width:450px" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mạch thực tế :</strong></p>\r\n\r\n<p><img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00164.jpg" style="height:338px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>lập tr&igrave;nh :</strong>&nbsp;<img alt="Thiết kế mạch Demo led cube 8x8x8" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/laptop00165.jpg" style="height:338px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Video demo :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bản ch&iacute;nh thức sẽ ra mắt trong thời gian sớm nhất .&nbsp;</p>\r\n</div>', 'IC', 0, 'Trên thị trường hiện nay có rất nhiều loại IC với nhiều mẫu mã và chủng loại khác nhau. Tuy nhiên, có rất nhiều con IC mang nhãn hiệu nổi tiếng nhưng chất lượng lại rất kém cỏi. Sẽ rất nguy hiểm nếu chúng ta mua phải những con IC đó. Vậy có cách nào nhận biết IC chất lượng cao ngay khi mua không? Sau đây chúng tôi sẽ hướng dẫn các bạn cách nhận biết.'),
(14, 'Tăng công suốt cho IC M720', '<p><strong>Như ch&uacute;ng ta đ&atilde; biết để việc tạo ra d&ograve;ng điện l&agrave; bước tiến vĩ đại trong lịch sử lo&agrave;i người .Để sản xuất ra d&ograve;ng điện xoay chiều c&oacute; rất nhiều phương ph&aacute;p như thủy điện,nhiệt điện .</strong></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image001_1.jpg" style="height:417px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Phần I</strong><br />\r\n<em><strong>Cơ sở l&yacute; thuyết</strong></em></p>\r\n\r\n<p>Nh&igrave;n chung c&aacute;c phương ph&aacute;p tr&ecirc;n &aacute;p dụng cho sản xuất điện c&ocirc;ng nghiệp , c&ograve;n với c&aacute;c hộ gia đ&igrave;nh khi nguồn điện c&ocirc;ng nghiệp bị cắt phương ph&aacute;p chủ yếu được sử dụng ở đ&acirc;y l&agrave; nghịch lưu d&ograve;ng điện một chiều th&agrave;nh xoay chiều. Dựa tr&ecirc;n phương ph&aacute;p đ&oacute; nh&oacute;m đưa ra sơ đồ nguy&ecirc;n l&yacute; tạo điện &aacute;p xoay chiều từ d&ograve;ng một chiều.</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image002_1.jpg" style="height:203px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>I,Khối nguồn</strong><br />\r\nNguồn điện được sử dụng ở đ&acirc;y l&agrave; nguồn điện một chiều lấy từ b&igrave;nh ắc quy .Thời gian sử dụng phụ thuộc chủ yếu v&agrave;o dung<br />\r\nlượng lưu trữ của ắc quy .C&ocirc;ng thức t&iacute;nh c&ocirc;ng suất ph&aacute;t của acquy như sau: P=u.i<br />\r\nv&iacute; dụ: acquy 12v 100Ah th&igrave; c&ocirc;ng suất ph&aacute;t :12.100=1200w<br />\r\nNếu chạy b&oacute;ng đ&egrave;n compac 20w sẽ được 60 giờ .</p>\r\n\r\n<p><strong>II, Khối tạo tần số 50Hz</strong><br />\r\nNhiệm vụ của khối tạo ra s&oacute;ng dao động đưa v&agrave;o khối c&ocirc;ng suất với tần số điện c&ocirc;ng nghiệp. S&oacute;ng ở đ&acirc;y thường l&agrave; hai dạng ch&iacute;nh l&agrave; h&igrave;nh sin hoặc vu&ocirc;ng .Thường th&igrave; khối c&ocirc;ng suất trở kh&aacute;ng đầu v&agrave;o rất nhỏ n&ecirc;n tr&ecirc;n thực tế ch&uacute;ng ta cần một khối khuyếch đại đệm nhiệm vụ ổn định khối ph&aacute;t xung dao động giảm trở kh&aacute;ng đầu v&agrave;o cho tầng c&ocirc;ng suất.</p>\r\n\r\n<p><strong>III,Khối c&ocirc;ng suất</strong><br />\r\nTừ dạng s&oacute;ng nhận được từ khối ph&aacute;t khối c&ocirc;ng suất sẽ khuyếch đại đưa đến biến &aacute;p tạo điện &aacute;p xoay chiều. Thường th&igrave; khối n&agrave;y sử dụng c&aacute;c linh kiện c&ocirc;ng suất như thysistor transistor chịu d&ograve;ng lớn như D718,2N3055&hellip;.y&ecirc;u cầu cho khối n&agrave;y hoạt động tốt cần c&oacute; hệ thống tản nhiệt l&agrave;m m&aacute;t.</p>\r\n\r\n<p>IV,Biến &aacute;p nghịch lưu<br />\r\nĐ&acirc;y l&agrave; th&agrave;nh phần ch&iacute;nh quyết định tới c&ocirc;ng suất ph&aacute;t của mạch. Biến &aacute;p được sử dụng l&agrave; biến &aacute;p nghịch lưu c&oacute; tỷ số v&ograve;ng d&acirc;y của cuộn thứ cấp lớn hơn rất nhiều cuộn sơ cấp.C&ocirc;ng suất của mạch được t&iacute;nh như sau: Pmax=U.I<br />\r\nVới I l&agrave; d&ograve;ng điện biến &aacute;p chịu được<br />\r\nU l&agrave; hiệu điện thế đặt v&agrave;o cuộn sơ cấp<br />\r\nV&iacute; dụ một biến &aacute;p nghịch lưu 12V220V d&ograve;ng điện 40A<br />\r\nC&ocirc;ng suất tối đa của mạch sẽ l&agrave;:12.40=480W chạy được một ti vi 2 quạt v&agrave; 3 b&oacute;ng t&iacute;p 40W</p>\r\n\r\n<p><strong>Phần II</strong></p>\r\n\r\n<p><em><strong>Sơ đồ mạch</strong></em></p>\r\n\r\n<p>C&aacute;c bạn c&oacute; thể thực hiện theo 1 trong c&aacute;c sơ đồ sau :</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/dientu00045.jpg" style="height:265px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00046_1.jpg" style="height:244px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00047.jpg" style="height:293px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00048.jpg" style="height:264px; margin:0px; width:450px" /></p>\r\n\r\n<p>C&ograve;n đ&acirc;y l&agrave; mạch m&igrave;nh thiết kế trong đồ &aacute;n :</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image005.jpg" style="height:204px; margin:0px; width:510px" /></p>\r\n\r\n<p>Ch&uacute;ng ta cần quan t&acirc;m đến IC CD4047<br />\r\n<strong>Chức năng:</strong>&nbsp;tạo s&oacute;ng vu&ocirc;ng hai nửa chu kỳ.<br />\r\nĐ&acirc;y l&agrave; ic gồm 14 ch&acirc;n đ&oacute;ng g&oacute;i dạng dip 100T. Điện &aacute;p hoạt động trong khoảng từ 3 đến 15 V. Ch&uacute;ng ta cần quan t&acirc;m tới chức năng của c&aacute;c ch&acirc;n sau:<br />\r\nCh&acirc;n 1 đầu v&agrave;o tụ C<br />\r\nCh&acirc;n 2 đầu v&agrave;o điện trở R<br />\r\nCh&acirc;n 3 đầu v&agrave;o R-C tạo dao động với tần số định sẵn<br />\r\nCh&acirc;n 10 đầu ra xung vu&ocirc;ng b&aacute;n chu kỳ dương<br />\r\nCh&acirc;n 11 đầu ra xung vu&ocirc;ng b&aacute;n chu kỳ &acirc;m<br />\r\nCh&acirc;n 7 cấp nguồn &acirc;m<br />\r\nCh&acirc;n 14 cấp nguồn dương<br />\r\nTần số của xung vu&ocirc;ng ra được t&iacute;nh theo c&ocirc;ng thức:</p>\r\n\r\n<p>T= 2,.48RC<img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00049.jpg" style="height:180px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Ph&acirc;n t&iacute;ch mạch điện :</strong><br />\r\nĐầu ti&ecirc;n IC 4047 ph&aacute;t ra s&oacute;ng vu&ocirc;ng với hai nửa chu kỳ.Với C=0,1uF ta điều chỉnh biến trở để tần số hoạt động l&agrave; 50Hz .<br />\r\nT&iacute;n hiệu s&oacute;ng vu&ocirc;ng được xuất ra tr&ecirc;n c&aacute;c ch&acirc;n 10,11 k&iacute;ch mở tran H1061,ở đ&acirc;y n&oacute; hoạt động như một kh&oacute;a điện tử.<br />\r\nTrasistor H1061 l&agrave; tran c&ocirc;ng suất tầm trung nhiệm vụ trong mạch k&iacute;ch mở tầng tran c&ocirc;ng suất lớn. Cụ thể khi c&oacute; t&iacute;n hiệu từ Ic lm358 đưa v&agrave;o tran mở khi đ&oacute; điện &aacute;p của t&iacute;n hiệu được khuyếch đại đủ để k&iacute;ch mở cặp tran D718<br />\r\nCặp tran c&ocirc;ng suất D718 hoạt động như một kh&oacute;a điện tử đ&oacute;ng ngắt li&ecirc;n tục với tần số 50Hz của bộ ph&aacute;t xung .Từ sự đ&oacute;ng ngắt n&agrave;y d&ograve;ng điện qua biến &aacute;p thay đổi theo dạng t&iacute;n hiệu s&oacute;ng vu&ocirc;ng .Ở chu kỳ dương cuộn L1 của phần sơ cấp được cấp nguồn , điện &aacute;p ở đầu ra được khuyếch đại theo tỷ số v&ograve;ng d&acirc;y của biến &aacute;p. L&uacute;c n&agrave;y đầu ra l&agrave; chu kỳ dương .Ngược lại ở chu kỳ &acirc;m cuộn L2 của phần sơ cấp được cấp điện ,điện &aacute;p đầu ra l&agrave; chu kỳ &acirc;m của t&iacute;n hiệu.<br />\r\nDo đ&oacute; đầu ra của biến &aacute;p điện &aacute;p l&agrave; d&ograve;ng điện xoay chiều tần số 50Hz ,dạng s&oacute;ng vu&ocirc;ng.</p>\r\n\r\n<p><strong>Mạch điện thực tế :</strong><br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="Mạch nghịch lưu 1 pha 12V to 220V" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/dientu00050.jpg" style="height:562px; margin:0px; width:441px" /></p>', 'IC', 0, 'IC M720 là một loại IC được dùng khá phổ biến, đặc biệt là trong các mạch lắp song song. Nếu biết cách tối ưu cho IC này, chúng ta có thể tăng công suốt của nó từ 10% đến 30 %. Sau đây chúng tôi sẽ hướng dẫn các bạn cách tăng công suốt cho IC này.'),
(15, 'Dùng IC với dòng 3 chiều', '<p><strong>Ch&uacute;ng ta biết tr&ecirc;n thị trường lu&ocirc;n c&oacute; b&aacute;n c&aacute;c ic ổn &aacute;p 3 ch&acirc;n họ 78xx, họ 79xx. Vậy nếu muốn c&oacute; mức &aacute;p DC ngả ra ổn định, Bạn t&igrave;m v&agrave; d&ugrave;ng c&aacute;c ic ổn &aacute;p n&agrave;y</strong>.</p>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px">&nbsp;</div>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px"><img alt="Mạch nguồn 5V có mức ổn định tốt với IC 7805" src="http://hc.serveftp.com/uploads/dien-tu/2013_08/machdien00002.jpg" style="height:215px; margin:0px; width:450px" /></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px">Ch&uacute;ng ta&nbsp;biết tr&ecirc;n thị trường lu&ocirc;n c&oacute; b&aacute;n c&aacute;c ic ổn &aacute;p 3 ch&acirc;n họ 78xx,&nbsp;họ 79xx. Vậy nếu muốn c&oacute; mức &aacute;p DC ngả ra ổn định, Bạn t&igrave;m&nbsp;v&agrave; d&ugrave;ng c&aacute;c ic ổn &aacute;p n&agrave;y. Với ic ổn &aacute;p 7805, mức &aacute;p ra l&agrave;&nbsp;5V, với ic ổn &aacute;p 7809, mức &aacute;p ra sẽ l&agrave; 9V, với ic ổn &aacute;p 7812,&nbsp;<br />\r\nmức &aacute;p ra sẽ l&agrave; 12V...IC ổn &aacute;p họ 79xx d&ugrave;ng tạo ổn &aacute;p tr&ecirc;n&nbsp;đường nguồn volt &acirc;m.</div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div style="background:rgb(240, 240, 240); border:0px; padding:0px">&nbsp; &nbsp;Trong&nbsp;mạch, ch&uacute;ng ta d&ugrave;ng tụ h&oacute;a lớn C1 tạo ổn &aacute;p tr&ecirc;n đường nguồn&nbsp;DC, đ&acirc;y l&agrave; dạng ổn &aacute;p thụ động, ch&uacute;ng ta d&ugrave;ng ic ổn &aacute;p 7805&nbsp;để c&oacute; mức &aacute;p ra 5V c&oacute; độ ổn định rất tốt, đ&acirc;y l&agrave; dạng ổn&nbsp;&aacute;p t&iacute;ch cực. Khi d&ugrave;ng ic ổn &aacute;p họ 78xx, họ 79xx, tr&ecirc;n ngả ra.&nbsp;<br />\r\n&nbsp; Bạn nhớ gắn th&ecirc;m tụ h&oacute;a d&ugrave;ng để tr&aacute;nh hiện tượng ph&aacute;t sinh&nbsp;dao động tự k&iacute;ch, khi mạch ổn &aacute;p trong ic bị dao động, Bạn sẽ&nbsp;thấy mức &aacute;p DC tr&ecirc;n ngả ra chập chờn l&uacute;c l&ecirc;n l&uacute;c xuống. Trường&nbsp;hợp đường nguồn 5V n&agrave;y d&ugrave;ng cấp điện cho c&aacute;c mạch điện l&agrave;m&nbsp;việc ở v&ugrave;ng tần số cao, l&uacute;c đ&oacute; Bạn phải gắn th&ecirc;m tụ nhỏ C3,&nbsp;<br />\r\nc&ocirc;ng dụng của c&aacute;c tụ nhỏ l&agrave; lọc bỏ c&aacute;c t&iacute;n hiệu tần số cao&nbsp;rất tốt, trong khi đ&oacute; do cấu tr&uacute;c b&ecirc;n trong của c&aacute;c tụ h&oacute;a&nbsp;lớn c&oacute; tiềm ẩn t&iacute;nh ống d&acirc;y, cuộn cảm n&ecirc;n kh&ocirc;ng lọc tốt c&aacute;c&nbsp;d&ograve;ng điện t&iacute;n hiệu tần số cao nhiễm tr&ecirc;n đường nguồn. Trong&nbsp;mạch ch&uacute;ng ta cũng d&ugrave;ng Led đỏ, Led xanh để l&agrave;m Led chỉ thị.</div>', 'IC', 0, 'Việc sáng chế ra dòng điện 3 chiều là một bước tiễn vĩ đại trong lịch sử nhân loại nói chung và lịch sử ngành điện nói riêng. Vậy làm thế nào để sử dụng IC trong mạch có dòng 3 chiều, sau đây chúng tôi sẽ hướng dẫn các bạn cách sử dụng IC trong mạch có dòng 3 chiều.'),
(16, 'Cách không để IC bị biến màu', '<p><strong>Mạch nghịch lưu d&ugrave;ng 4047 Haicong.vn đ&atilde; lắp nhiều rồi , thường được ưu điểm xung ra ổn định nhưng nhược điểm l&agrave; ic kh&ocirc;ng chịu được d&ograve;ng cao hay ch&aacute;y.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch inverter 500W dùng transistor đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui3.jpg" style="height:181px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;Ngược lại với th&igrave; tranistor tỏ ra kh&aacute; ổn định ,dễ lắp , đặc biệt&nbsp;Haicong.vn đ&atilde; test thử 2 mạch tr&ecirc;n 1 biến &aacute;p th&igrave; c&ocirc;ng suất của tran lớn hơn . Đ&acirc;y l&agrave; mạch nghịch lưu 500W . lưu &yacute; : biến &aacute;p sơ cấp quấn d&acirc;y 3mm &aacute;p t&acirc;m 11 V , biến trở 100K để chỉnh đ&uacute;ng tần số 50Hz , tran Cs D718X5 mỗi vế , th&ecirc;m mạch lọc LC .Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</p>\r\n</div>\r\n</div>', 'IC', 0, 'IC dùng được 1 thời gian thường không giữ được màu sắc như lúc ban đầu. Nếu biết cách giữ gìn bạn có thể hạn chế điều này. Có nhiều dung dịch để giữ màu cho IC, nước muối là một điển hình. Sau đây chúng tôi sẽ hướng dẫn các bạn cách sử dụng nước muối để giữ màu cho IC'),
(17, 'Kinh nghiệm sử dụng PIN lâu bền', '<p><strong>Truyền h&igrave;nh mầu ra đời khi truyền h&igrave;nh đen trắng đ&atilde; trưởng th&agrave;nh v&agrave; vẫn c&ograve;n đang được sử dụng rộng d&atilde;i, v&igrave; vậy khi x&acirc;y dựng truyền h&igrave;nh mầu th&igrave; to&agrave;n bộ hệ thống truyền h&igrave;nh đen trắng vẫn được giữa nguy&ecirc;n v&agrave; người ta chỉ truyền th&ecirc;m c&aacute;c t&iacute;n hiệu mầu.</strong></p>\r\n\r\n<p>C&aacute;c t&iacute;n hiệu FM tiếng, t&iacute;n hiệu đồng bộ d&ograve;ng v&agrave; đồng bộ m&agrave;nh kh&ocirc;ng thay đổi, ri&ecirc;ng t&iacute;n hiệu Video nay được đổi th&agrave;nh hai t&iacute;n hiệu l&agrave; Y v&agrave; C, Y l&agrave; t&iacute;n hiệu ch&oacute;i mang th&ocirc;ng tin về h&igrave;nh ảnh đen trắng v&agrave; C l&agrave; s&oacute;ng mang phụ mang th&ocirc;ng tin về t&iacute;n hiệu mầu :</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00001.jpg" style="height:286px; margin:0px; width:450px" /></p>\r\n\r\n<p>Tổng qu&aacute;t về kỹ thuật truyền h&igrave;nh mầu .</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Như vậy trong t&iacute;n hiệu truyền h&igrave;nh mầu th&igrave; t&iacute;n hiệu Video tổng hợp bao gồm :</p>\r\n\r\n<p>+ T&iacute;n hiệu ch&oacute;i k&yacute; hiệu l&agrave; Y - mang th&ocirc;ng tin về h&igrave;nh ảnh đen trắng, đ&acirc;y ch&iacute;nh l&agrave; t&iacute;n hiệu Video được giữ lại khi ph&aacute;t triển truyền h&igrave;nh mầu, nhằm tương th&iacute;ch với c&aacute;c m&aacute;y thu h&igrave;nh đen trắng.</p>\r\n\r\n<p>+ T&iacute;n hiệu C l&agrave; s&oacute;ng mang phụ, mang th&ocirc;ng tin về mầu sắc</p>\r\n\r\n<p>+ T&iacute;n hiệu FM l&agrave; s&oacute;ng mang điều tần của t&iacute;n hiệu tiếng</p>\r\n\r\n<p>+ Xung H.syn l&agrave; xung đồng bộ d&ograve;ng</p>\r\n\r\n<p>+ Xung V.syn l&agrave; xung đồng bộ m&agrave;nh.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; So với truyền h&igrave;nh đen trắng th&igrave; t&iacute;n hiệu Y l&agrave; t&iacute;n hiệu thị tần, xung H.syn, xung V.syn, v&agrave; t&iacute;n hiệu FM l&agrave; kh&ocirc;ng thay đổi, như vậy truyền h&igrave;nh mầu thực chất l&agrave; truyền h&igrave;nh đen trắng c&oacute; th&ecirc;m t&iacute;n hiệu s&oacute;ng mang mầu C , điều n&agrave;y c&oacute; nghĩa l&agrave; tất cả c&aacute;c kiến thức về truyền h&igrave;nh đen trắng đều được tận dụng lại, v&igrave; vậy hiểu được truyền h&igrave;nh đen trắng sẽ gi&uacute;p bạn hiểu truyền h&igrave;nh mầu dễ d&agrave;ng hơn.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00002.gif" style="height:190px; margin:0px; width:312px" /></p>\r\n\r\n<p>Phổ t&iacute;n hiệu Video tổng hợp</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; T&iacute;n hiệu C ( s&oacute;ng mang mầu ) được tạo ra bằng c&aacute;ch n&agrave;o ? t&iacute;n hiệu mầu R, G, B&nbsp; l&agrave; g&igrave; ? c&aacute;c c&acirc;u hỏi đ&oacute; sẽ được giải đ&aacute;p sau khi ta t&igrave;m hiểu về bản chất &aacute;nh s&aacute;ng v&agrave; mầu sắc cũng như một số đặc điểm của mắt người.</p>\r\n\r\n<p><strong>&nbsp;2/ T&iacute;nh chất của &aacute;nh s&aacute;ng v&agrave; mầu sắc</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Mục đ&iacute;ch của b&agrave;i n&agrave;y l&agrave; để t&igrave;m hiểu nguồn gốc của t&iacute;n hiệu C ( Choma_) l&agrave; s&oacute;ng mang phụ của t&iacute;n hiệu mầu, v&agrave; t&igrave;m hiểu c&aacute;c t&iacute;n hiệu mầu.</p>\r\n\r\n<p><em><strong>&nbsp;&nbsp;&nbsp;&nbsp; 2.1 - Bản chất kh&aacute;ch quan của &aacute;nh s&aacute;ng</strong></em></p>\r\n\r\n<p>&nbsp;&nbsp; &Aacute;nh s&aacute;ng về phương diện vật l&yacute; kh&aacute;ch quan chỉ l&agrave; s&oacute;ng điện từ c&oacute; tần số từ 3,8 x 1014&nbsp;đến 7,8 x 1014&nbsp; Hz, hay c&oacute; bước s&oacute;ng từ 380nm đến 780nm như vậy &aacute;nh s&aacute;ng chỉ chiếm một khoảng rất hẹp trong dải s&oacute;ng điện từ, dải đ&oacute; ta gọi l&agrave; phổ &aacute;nh s&aacute;ng.</p>\r\n\r\n<p><em><strong>&nbsp;&nbsp; 2.2 Cảm nhận chủ quan của mắt người</strong></em></p>\r\n\r\n<p>&nbsp; M&agrave;u sắc ho&agrave;n to&agrave;n l&agrave; cảm gi&aacute;c chủ quan của con người</p>\r\n\r\n<p>&nbsp;Trong to&agrave;n bộ phổ &aacute;nh s&aacute;ng từ 380nm đến 780nm sẽ cho cảm gi&aacute;c ở mắt người l&agrave; nguồn s&aacute;ng trắng, thực tế phổ &aacute;nh s&aacute;ng n&agrave;y l&agrave; t&acirc;p hợp của rất nhiều &aacute;nh s&aacute;ng đơn sắc.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00003.gif" style="height:59px; margin:0px; width:393px" /></p>\r\n\r\n<p>Mầu phổ</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp; Mầu của vật</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Thực ra một vật ( kh&ocirc;ng phải l&agrave; nguồn s&aacute;ng ) th&igrave; kh&ocirc;ng c&oacute; mầu, v&iacute; dụ một vật bất kỳ nếu ta kh&ocirc;ng chiếu &aacute;nh s&aacute;ng v&agrave;o n&oacute; th&igrave; ta kh&ocirc;ng nh&igrave;n thấy vật đ&oacute;.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;<strong>Th&iacute; nghiệm :</strong>&nbsp;Trong ph&ograve;ng tối, nếu ta chiếu &aacute;nh s&aacute;ng trắng v&agrave;o tờ giấy th&igrave; ta thấy tờ giấy mầu trắng, nếu ta chiếu &aacute;nh s&aacute;ng đơn sắc mầu đỏ v&agrave;o tờ giấy ta lại thấy tờ giấy mầu đỏ, chiếu &aacute;nh s&aacute;ng xanh ta lại thấy tờ giấy mầu xanh =&gt; Chứng tỏ mầu của vật chỉ đ&uacute;ng khi c&oacute; một nguồn s&aacute;ng trắng chiếu v&agrave;o.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Một nguồn s&aacute;ng trắng l&agrave; tập hợp của v&ocirc; số nguồn s&aacute;ng đơn sắc, khi chiếu v&agrave;o một vật n&agrave;o đ&oacute; th&igrave; một số bước s&oacute;ng bị vật đ&oacute; hấp thụ ho&agrave;n to&agrave;n hay một phần, phần c&ograve;n lại phản chiếu đến mắt cho ta cảm gi&aacute;c về một mầu n&agrave;o đ&oacute;.</p>\r\n\r\n<p><strong>&nbsp;&nbsp; C&aacute;c đặc t&iacute;nh x&aacute;c định một mầu<br />\r\n&nbsp;&nbsp;&nbsp;</strong>Một mầu được x&aacute;c định dựa tr&ecirc;n 3 yếu tố l&agrave; :</p>\r\n\r\n<p><strong>Sắc th&aacute;i của mầu</strong>&nbsp;: yếu tố n&agrave;y để ph&acirc;n biệt c&aacute;c mầu sắc kh&aacute;c nhau .</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00004.gif" style="height:117px; margin:0px; width:257px" /></p>\r\n\r\n<p>Hai mầu c&oacute; sắc th&aacute;i kh&aacute;c nhau c&oacute; phổ kh&aacute;c nhau</p>\r\n\r\n<p><strong>Độ ch&oacute;i của mầu</strong>&nbsp;: độ ch&oacute;i mầu l&agrave; cường độ s&aacute;ng của mầu đ&oacute; mạnh hay yếu.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00005.gif" style="height:164px; margin:0px; width:169px" /></p>\r\n\r\n<p>Hai mầu c&ugrave;ng sắc th&aacute;i nhưng c&oacute; độ ch&oacute;i kh&aacute;c nhau<br />\r\ncho ta cường độ s&aacute;ng kh&aacute;c nhau</p>\r\n\r\n<p><strong>Độ b&atilde;o ho&agrave; mầu</strong>&nbsp;: Chỉ độ tinh khiết của mầu so với mầu trắng, mầu trắng c&oacute; độ b&atilde;o ho&agrave; mầu bằng 0.</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00006.gif" style="height:141px; margin:0px; width:257px" /></p>\r\n\r\n<p>Hai mầu c&ugrave;ng sắc th&aacute;i nhưng c&oacute; độ b&atilde;o ho&agrave; mầu kh&aacute;c nhau,<br />\r\n&nbsp;mầu c&oacute; độ b&atilde;o ho&agrave; c&agrave;ng cao th&igrave; phổ c&agrave;ng hẹp</p>\r\n\r\n<p><em>&nbsp;<strong>&nbsp;2.3 Cấu tạo của mắt người</strong></em></p>\r\n\r\n<p>&nbsp; Bằng c&aacute;c nghi&ecirc;n cứu về cấu tạo của mắt người, c&aacute;c nh&agrave; khoa học đ&atilde; nhận thấy rằng, mắt người k&eacute;m nhậy với c&aacute;c mầu đơn sắc, khi c&oacute; một nguồn s&aacute;ng l&agrave; tập hợp của nhiều mầu đơn sắc th&igrave; mắt ngư&ograve;i kh&ocirc;ng thể ph&acirc;n biệt được c&aacute;c mầu đơn sắc đ&oacute; m&agrave; c&aacute;c tế b&agrave;o thần kinh của mắt cho ta cảm gi&aacute;c về một mầu kh&aacute;c<br />\r\n&nbsp;&nbsp; Th&iacute; dụ : nếu chiếu một ch&ugrave;m s&aacute;ng c&oacute; đầy đủ c&aacute;c mầu đơn sắc gồm Đỏ, cam, v&agrave;ng, lục , lam , lơ , t&iacute;m&nbsp; v&agrave;o mắt th&igrave; ta c&oacute; cảm nhận đ&oacute; l&agrave; mầu trắng,&nbsp; nếu chiếu hai mầu đơn sắc l&agrave; đỏ v&agrave; xanh l&aacute; th&igrave; ta cảm nhận đ&oacute; l&agrave; mầu v&agrave;ng.</p>\r\n\r\n<p><strong><em>&nbsp; 2.4&nbsp;&nbsp;&nbsp; Ba mầu sắc cơ bản trong tự nhi&ecirc;n.&nbsp;</em></strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; Bằng c&aacute;c thực nghiệm người ta chứng minh được rằng, trong phổ &aacute;nh s&aacute;ng c&oacute; ba mầu đơn sắc c&oacute; đặc điểm , từ ba mầu đ&oacute; c&oacute; thể tổng hợp th&agrave;nh một mầu bất kỳ ( mầu bất kỳ l&agrave; cảm nhận của mắt ) v&agrave; ngược lại một mầu bất kỳ ta cũng c&oacute; thể ph&acirc;n t&iacute;ch th&agrave;nh ba mầu cơ bản đ&oacute; , ba mầu cơ bản đ&oacute; l&agrave; .</p>\r\n\r\n<p>+ Mầu đỏ&nbsp; : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; R ( Red )</p>\r\n\r\n<p>+ Mầu xanh l&aacute; : G ( Green )</p>\r\n\r\n<p>+ Mầu xanh lơ : B ( Blue )</p>\r\n\r\n<p>&nbsp; Ba mầu sắc n&agrave;y sẽ được ứng dụng trong kỹ thuật truyền h&igrave;nh v&agrave; trong c&aacute;c thiết bị c&oacute; h&igrave;nh mầu như m&aacute;y in mầu, điện thoại di động, m&aacute;y vi t&iacute;nh v v...</p>\r\n\r\n<p><em><strong>&nbsp; 2.5&nbsp; Nguy&ecirc;n l&yacute; trộn mầu</strong></em></p>\r\n\r\n<p><strong>&nbsp; Th&iacute; nghiệm&nbsp;</strong>:&nbsp; C&oacute; ba nguồn s&aacute;ng đơn sắc ph&aacute;t ra ba mầu : Đỏ, Xanh l&aacute;, Xanh lơ&nbsp; c&ugrave;ng chiếu l&ecirc;n một ph&ocirc;ng mầu trắng, ta h&atilde;y quan s&aacute;t mầu sắc tại c&aacute;c vị tr&iacute; m&agrave; c&aacute;c mầu giao nhau :</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00007.gif" style="height:218px; margin:0px; width:395px" /></p>\r\n\r\n<p>Nguy&ecirc;n l&yacute; trộn mầu từ ba mầu cơ bản R , G , B</p>\r\n\r\n<p>+ Ba nguồn s&aacute;ng tr&ecirc;n c&oacute; cường độ bằng nhau v&agrave; bằng 100%<br />\r\n&nbsp;&nbsp; R = G = B = 100%</p>\r\n\r\n<p>+ R + G =&nbsp; V&agrave;ng&nbsp;&nbsp; ( Đỏ + Xanh l&aacute; = Mầu v&agrave;ng )</p>\r\n\r\n<p>+ R + G + B = Trắng ( Đỏ + Xanh l&aacute; + Xanh lơ = Trắng )</p>\r\n\r\n<p>+ R + B = T&iacute;m ( Đỏ + Xanh lơ = T&iacute;m )</p>\r\n\r\n<p>+ G + B = Xanh dương ( Xanh l&aacute; + Xanh lơ = Xanh dương )</p>\r\n\r\n<p><img alt="Tổng quát về kỹ thuật truyền hình mầu" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/tuyenhinh00008.gif" style="height:203px; margin:0px; width:232px" /></p>\r\n\r\n<p>Trộn từ 3 mầu cơ bản R, G , B&nbsp; với c&aacute;c<br />\r\ncường độ s&aacute;ng kh&aacute;c nhau</p>', 'PIN', 1, 'PIN dùng đươc một thời gian thường chai rất nhanh, nếu không biết cách giữ gìn, một cục PIN 6 cell có thể chỉ có tuổi thọ 6 tháng. Vậy làm cách nào để giữ gìn PIN tốt và sử dụng được lâu bền. Sau đây mình sẽ hướng dẫn các bạn cách sử dụng PIN được lâu và bền'),
(18, 'Hướng dẫn tăng công suốt của PIN', '<p><strong>1. Kh&aacute;i niệm về từ trường.&nbsp;</strong><br />\r\n<strong>&nbsp; Nam ch&acirc;m v&agrave; từ t&iacute;nh .</strong><br />\r\nTrong tự nhi&ecirc;n c&oacute; một số chất c&oacute; thể h&uacute;t được sắt gọi l&agrave; nam ch&acirc;m tự nhi&ecirc;n.<br />\r\nTrong c&ocirc;ng nghiệm người ta luyện th&eacute;p hoặc hợp chất th&eacute;p để tạo th&agrave;nh nam ch&acirc;m nh&acirc;n tạo.<br />\r\nNam ch&acirc;m lu&ocirc;n lu&ocirc;n c&oacute; hai cực l&agrave; cực bắc North (N) v&agrave; cực nam South (S) , nếu chặt thanh nam ch&acirc;m ra l&agrave;m 2 th&igrave; ta lại được hai nam ch&acirc;m mới cũng c&oacute; hai cực N v&agrave; S &ndash; đ&oacute; l&agrave; nam ch&acirc;m c&oacute; t&iacute;nh chất kh&ocirc;ng ph&acirc;n chia..<br />\r\nNam ch&acirc;m thường được ứng dụng để sản xuất loa điện động, micro hoặc m&ocirc; tơ DC.<br />\r\n<br />\r\n<strong>&nbsp; Từ trường&nbsp;</strong><br />\r\nTừ trường l&agrave; v&ugrave;ng kh&ocirc;ng gian xung quanh nam ch&acirc;m c&oacute; t&iacute;nh chất truyền lực từ l&ecirc;n c&aacute;c vật liệu c&oacute; từ t&iacute;nh, từ trường l&agrave; tập hợp của c&aacute;c đường sức đi từ Bắc đến cực nam.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/namcham1.gif" style="height:124px; margin:0px; width:232px" /></div>\r\n\r\n<p><strong>&nbsp; Cường độ từ trường&nbsp;</strong><br />\r\nL&agrave; đại lượng đặc trưng cho độ mạnh yếu của từ trường, k&yacute; hiệu l&agrave; H đơn vị l&agrave; A/m</p>\r\n\r\n<p><strong>&nbsp; Độ từ cảm</strong><br />\r\nL&agrave; đại lượng đặc trưng cho vật c&oacute; từ t&iacute;nh chịu t&aacute;c động của từ trường, độ từ cảm phụ thuộc v&agrave;o vật liệu . VD&nbsp; Sắt c&oacute; độ từ cảm mạnh hơn đồng nhiều lần . Độ từ cảm được t&iacute;nh bởi c&ocirc;ng thức</p>\r\n\r\n<p>B = &micro;.H</p>\r\n\r\n<p>Trong đ&oacute; B : l&agrave;&nbsp; độ từ cảm<br />\r\n&micro; : l&agrave; độ từ thẩm<br />\r\nH :&nbsp; l&agrave; cường độ từ trường</p>\r\n\r\n<p><strong>&nbsp; Từ th&ocirc;ng</strong><br />\r\nL&agrave; số đường sức đi qua một đơn vị diện t&iacute;ch, từ th&ocirc;ng tỷ lệ thuật với cường độ từ trường.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/tuthong.png" style="height:131px; margin:0px; width:232px" /></div>\r\n\r\n<p><strong>&nbsp; Ứng dụng của Nam ch&acirc;m vĩnh cửu.</strong><br />\r\nNam ch&acirc;m vĩnh cửu được ứng dụng nhiều trong thiết bị điện tử, ch&uacute;ng được d&ugrave;ng để sản xuất Loa, Micro v&agrave; c&aacute;c loại M&ocirc; tơ DC.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/speaker.png" style="height:120px; margin:0px; width:120px" /></div>', 'PIN', 1, 'PIN được nhà sản xuất cố định công suốt, nhưng nếu biết cách chúng ta vẫn có thể ép nó hoạt động với công suốt lớn hơn rất nhiều. Sau đây chúng tôi sẽ hướng dẫn các bạn cách tăng công suốt của PIN.'),
(19, 'Sự khác nhau giữa PIN con thỏ và con sói', '<p><strong>TDA1524A, v&agrave; phần mạch khuếch đại c&ocirc;ng suất d&ugrave;ng TDA LA 4440 với P=25w nghe cực k&igrave; ấm v&agrave; đ&atilde; ko bị &ugrave; r&egrave; ,với phần thiết kế đ&atilde; ghi r&otilde; chi tiết từng con linh kiện d&acirc;n diện tử ai m&ecirc; nhạc th&igrave; c&oacute; thể độ chế ri&ecirc;ng cho m&igrave;nh .</strong></p>\r\n\r\n<p><strong>Th&ocirc;ng số của mạch :</strong><br />\r\n+ Điện &aacute;p đầu v&agrave;o : 12VDC<br />\r\n+ T&iacute;n hiệu đầu v&agrave;o : L, R<br />\r\n+ T&iacute;n hiệu đầu ra : L, R</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">+ N&uacute;t điều chỉnh : Bass, Treble, Volume, Blance<img alt="Mạch PCB chỉnh Bass, Treble, Volume, Blance dùng IC" src="http://hc.serveftp.com/uploads/dien-tu/2012_03/chuyendoi0012.jpg" style="height:343px; margin:0px; width:450px" /></div>\r\n\r\n<p><img alt="Mạch PCB chỉnh Bass, Treble, Volume, Blance dùng IC" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/mach0009.jpg" style="height:347px; margin:0px; width:450px" /></p>', 'PIN', 1, 'PIN con thỏ và PIN con sói có nhiều điểm khác nhau. Một trong số đó là PIN con thỏ được làm từ sói và PIN con sói được làm từ thỏ. Tuy nhiên thỏ và sói không liên quan không ít nhưng đủ không nhiều  đến PIN con sói và PIN con thỏ. Sau đây là những điểm khác nhau cơ bản giữa 2 loại PIN này.'),
(20, 'PIN mặt trời bị thiếu điện - phải làm sao', '<p><strong>Mạch được tạo bởi 2 con IC 555 d&ugrave;ng để tạo dao động. Tần số được điều khiển bởi ch&acirc;n 5 của IC. Đầu ti&ecirc;n l&agrave; IC 1 được l&agrave;m việc xung quang tần số l&agrave; 1hz v&agrave; tụ 47uF được nạp điện v&agrave; sau đ&oacute; l&agrave; xả điện li&ecirc;n tục qu&aacute; tr&igrave;nh đ&oacute; cứ diễn ra li&ecirc;n tục như vậy.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao1.jpg" style="height:226px; margin:0px; width:223px" /></p>\r\n\r\n<p><strong>Đ&acirc;y l&agrave; mạch tạo &acirc;m thanh đơn giản d&ugrave;ng cho c&ograve;i b&aacute;o động cho cảnh s&aacute;t</strong></p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao2.jpg" style="height:207px; margin:0px; width:450px" /></p>\r\n\r\n<p>Mạch được tạo bởi 2 con IC 555 d&ugrave;ng để tạo dao động. Tần số được điều khiển bởi ch&acirc;n 5 của IC. Đầu ti&ecirc;n l&agrave; IC 1 được l&agrave;m việc xung quang tần số l&agrave; 1hz v&agrave; tụ 47uF được nạp điện v&agrave; sau đ&oacute; l&agrave; xả điện li&ecirc;n tục qu&aacute; tr&igrave;nh đ&oacute; cứ diễn ra li&ecirc;n tục như vậy. Tần số ra loa được điều chế bởi IC2 v&agrave; ta nghe được &acirc;m thanh ra loa.<br />\r\nHai biến trở VR1 v&agrave; VR2 d&ugrave;ng để điều chế tần số đầu ra cho Loa. Tần số cảng lớn th&igrave; tiếng h&uacute; c&ograve;i c&agrave;ng to. Đ&acirc;y l&agrave; mạch b&aacute;o động &aacute;nh s&aacute;ng. Được sử dụng trong ph&ograve;ng tối v&agrave; khi c&oacute; &aacute;nh s&aacute;ng lọt v&agrave;o th&igrave; mạch sẽ ph&aacute;t ra tiếng b&aacute;o cho ta biết l&agrave; c&oacute; &aacute;nh s&aacute;ng</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao3.jpg" style="height:216px; margin:0px; width:450px" /></p>\r\n\r\n<p>Nh&igrave;n v&agrave;o mạch tr&ecirc;n kh&aacute; l&agrave; đơn giản v&igrave; mạch chỉ sử dụng 1 con 555 để tạo dao động ph&aacute;t &acirc;m thanh ra loa v&agrave; 1 con LDR cảm biến &aacute;nh s&aacute;ng.<br />\r\n+ 555 ở đ&acirc;y l&agrave; con tạo dao động xung vu&ocirc;ng trong mạch n&agrave;y n&oacute; tạo dao động l&agrave; 1Khz cấp cho tải l&agrave; Loa<br />\r\n+ LDR l&agrave; cảm biến &aacute;nh s&aacute;ng. Khi kh&ocirc;ng c&oacute; anh s&aacute;ng th&igrave; cảm biến n&agrave;y c&oacute; gi&aacute; trị điện trở l&agrave; v&ocirc; c&ugrave;ng c&ograve;n khi c&oacute; &aacute;nh s&aacute;ng đủ mạch th&igrave; cảm biến c&oacute; gi&aacute; trị điện trở l&agrave; 0.<br />\r\nKhi c&oacute; &aacute;nh s&aacute;ng th&igrave; LDR sẽ c&oacute; điện trở bằng 0 khi đ&oacute; n&oacute; sẽ ph&acirc;n cực thuận cho con BC158 dẫn đến cấp điện &aacute;p v&agrave;o ch&acirc;n 4 của 555 l&agrave; mạch dao động 555 hoạt động v&agrave; ph&aacute;t &acirc;m thanh ra loa. C&ograve;n khi kh&ocirc;ng c&oacute; &aacute;nh s&aacute;ng th&igrave; LDR c&oacute; gi&aacute; trị điện trở v&ocirc; c&ugrave;ng do đ&oacute; n&oacute; ko ph&acirc;n cực được cho BC158 &gt; Kh&ocirc;ng c&oacute; t&iacute;n hiệu ra loa. Biến trở 100K d&ugrave;ng để điều chỉnh mức cường độ &aacute;nh s&aacute;ng cảnh b&aacute;o. Đ&acirc;y l&agrave; mạch b&aacute;o trộm nh&agrave; 4 trong 1. Sử dụng NE556 l&agrave;m cảnh b&aacute;o kết hợp với chu&ocirc;ng b&aacute;o động,</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao4.jpg" style="height:248px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Mạch n&agrave;y được hoạt động khi phải thỏa m&atilde;n 4 điều kiện kh&aacute;c sau đ&acirc;y :</strong><br />\r\n+ Khi đ&egrave;n s&aacute;ng th&igrave; cảm biến LDR1 được bật<br />\r\n+ Khi đ&egrave;n s&aacute;ng th&igrave; cảm biến LDR2 được bật<br />\r\n+ Khi mở cửa<br />\r\n+ Khi chạm v&agrave;o vật (H&igrave;nh vẽ)<br />\r\nNếu m&agrave; kẻ trộm bị gặp phải 1 trong 4 trường hợp tr&ecirc;n th&igrave; sẽ c&oacute; 1 tiếng chu&ocirc;ng b&aacute;o động cho người bit l&agrave; c&oacute; kẻ trộmĐ&acirc;y l&agrave; mạch kh&ocirc;ng chỉ cho ch&uacute;ng ta biết mức nước ở thời điểm hiện tại m&agrave; n&oacute; c&ograve;n cảnh b&aacute;o cho ch&uacute;ng ta khi mức nước đ&atilde; đầy bằng hiện thị l&ecirc;n c&aacute;c đ&egrave;n LED v&agrave; chu&ocirc;ng b&aacute;o động</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao5.jpg" style="height:285px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Mạch sử dụng IC CMOS CD4066</strong>&nbsp;để đ&oacute;ng cắt v&agrave; đưa t&iacute;n hiệu mức nước ra c&aacute;c LED (Tr&ecirc;n h&igrave;nh vẽ). Khi mức nước trong th&ugrave;ng kh&ocirc;ng c&oacute; nước th&igrave; điện trở 180K &ocirc;m được nối với mức thấp (GND) n&ecirc;n kh&ocirc;ng c&oacute; đ&egrave;n LED n&agrave;o s&aacute;ng. Khi c&oacute; mức nước v&agrave;o th&igrave; đầu ti&ecirc;n n&oacute; kết nối giữa S1 với nguồn dương th&ocirc;ng qua c&aacute;c ion trong nước khi đ&oacute; đ&oacute;ng kh&oacute;a S1 tạo cho đ&egrave;n LED 1 s&aacute;ng. Cứ như vậy th&igrave; c&aacute;c mức 2, 3, ,4 cũng như vậy mức nước đến đ&acirc;u th&igrave; đ&egrave;n sẽ s&aacute;ng đến đ&oacute;.<br />\r\nRi&ecirc;ng khi mức nước đ&atilde; đầy c&oacute; th&ecirc;m 1 chu&ocirc;ng cảnh b&aacute;o l&agrave; nước trong th&ugrave;ng đ&atilde; đầy. b&aacute;o hiệu cho con người l&agrave; nước đ&atilde; đầy th&ugrave;ng.</p>\r\n\r\n<p><img alt="Nhửng mạch báo động đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/machbao6.jpg" style="height:295px; margin:0px; width:450px" /></p>\r\n\r\n<p><strong>Đ&acirc;y l&agrave; mạch cảnh b&aacute;o mức nước</strong>&nbsp;đ&atilde; đầy hay b&aacute;o động cho 1 mức nước n&agrave;o đ&oacute;. Mạch sử dụng con 555 tạo dao động ph&aacute;t &acirc;m thanh ra loa. Tần số ra loa để b&aacute;o động sẽ l&agrave; tần số dao động của IC555 : Tần số l&agrave; : f = 1/(0.693*0.022.10^-6*(100.10^3+9.4.10^3) = ?. Tần số n&agrave;y kh&ocirc;ng được lớn hơn 20Khz v&igrave; như thế l&agrave;m sao m&agrave; tai người c&oacute; thể nghe thấy được.<br />\r\nHoạt động : Mạch kh&aacute; l&agrave; đơn giản gồm 1 con BC109C v&agrave; bộ cảm biến cũng kh&aacute; đơn giản l&agrave; cảm biến nước. Khi mức nước d&acirc;ng cao đến chỗ cảm biến nước th&igrave; l&uacute;c đ&oacute; d&ograve;ng điện từ nguồn được dẫn qua ion nước ph&acirc;n cực cho B của Transitor. Khi đ&oacute; transitor dẫn d&ograve;ng từ nguồn cấp nguồn cho 555. Dẫn đến 555 tạo dao động ph&aacute;t &acirc;m thanh ra loa. Khi mức nước kh&ocirc;ng chạm cảm biến th&igrave; B kh&ocirc;ng được ph&acirc;n cực ngắt nguồn cấp cho 555 n&ecirc;n loa sẽ kh&ocirc;ng ph&aacute;t t&iacute;n hiệu&nbsp;</p>\r\n</div>\r\n</div>', 'PIN', 1, 'PIN mặt trời có năng lượng được lấy từ mặt trời, tuy nhiên đôi lúc nó bị thiếu điện và hoạt động dưới công suốt. Lúc này chúng ta phải biết cách khắc phục nhược điểm chết người này.');
INSERT INTO `tbl_documents` (`doc_id`, `doc_title`, `doc_content`, `doc_type`, `doc_vip`, `doc_summary`) VALUES
(21, 'Cách sử dụng PIN tiếp khi hết điện', '<p><strong>Mạch nghịch lưu d&ugrave;ng 4047 Haicong.vn đ&atilde; lắp nhiều rồi , thường được ưu điểm xung ra ổn định nhưng nhược điểm l&agrave; ic kh&ocirc;ng chịu được d&ograve;ng cao hay ch&aacute;y.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch inverter 500W dùng transistor đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui3.jpg" style="height:181px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;Ngược lại với th&igrave; tranistor tỏ ra kh&aacute; ổn định ,dễ lắp , đặc biệt&nbsp;Haicong.vn đ&atilde; test thử 2 mạch tr&ecirc;n 1 biến &aacute;p th&igrave; c&ocirc;ng suất của tran lớn hơn . Đ&acirc;y l&agrave; mạch nghịch lưu 500W . lưu &yacute; : biến &aacute;p sơ cấp quấn d&acirc;y 3mm &aacute;p t&acirc;m 11 V , biến trở 100K để chỉnh đ&uacute;ng tần số 50Hz , tran Cs D718X5 mỗi vế , th&ecirc;m mạch lọc LC .Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</p>\r\n</div>\r\n</div>', 'PIN', 0, 'PIN dùng đươc một thời gian thường chai rất nhanh, nếu không biết cách giữ gìn, một cục PIN 6 cell có thể chỉ có tuổi thọ 6 tháng. Vậy làm cách nào để giữ gìn PIN tốt và sử dụng được lâu bền. Sau đây mình sẽ hướng dẫn các bạn cách sử dụng PIN được lâu và bền'),
(22, 'Hướng dẫn dùng PIN R78V', '<p><strong>L&agrave; ngưới th&iacute;ch khoa điện tử chắc ai cũng phải l&agrave;m cho m&igrave;nh một mạch nguồn để thực h&agrave;nh ! T&ugrave;y c&aacute;ch l&agrave;m của từng người nhưng cơ bản th&igrave; vẫn sử dụng c&aacute;c IC ổn &aacute;p họ 78 &amp; 79 , kết hợp với Ic Lm317 Lm337 để ra điện &aacute;p biến đổi .</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image001_3.jpg" style="height:332px; margin:0px; width:450px" /></p>\r\n\r\n<p>Về nguy&ecirc;n tắc th&igrave; ch&uacute;ng ta sử dụng diot cầu kết hợp c&aacute;c tụ điện cho ra điện &aacute;p đối xứng cấp cho từng cặp ic 78,79 .Th&ecirc;m phần bảo vệ cầu ch&igrave; hoặc role ch&uacute;ng ta đ&atilde; c&oacute; một bộ nguồn ổn định để suw3 dụng .<img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image002_3.jpg" style="height:192px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image003_3.jpg" style="height:236px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image004_2.jpg" style="height:419px; margin:0px; width:450px" /></p>\r\n\r\n<p><img alt="Mạch nguồn ổn áp đối xứng đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_05/clip_image005_2.jpg" style="height:276px; margin:0px; width:450px" /></p>\r\n</div>\r\n</div>\r\n\r\n<h2>&nbsp;</h2>', 'PIN', 0, 'PIN r78V làm một loại pin khá mới trên thị trường, nó được dùng để hỗ trợ năng lượng cho các mạch theo chuẩn IC332Y1. Sau đấy chúng tôi sẽ hướng dẫn các bạn cách sử dụng PIN này '),
(23, 'Cách biến PIN một chiều sang 2 chiều', '<p><strong>1. Kh&aacute;i niệm về từ trường.&nbsp;</strong><br />\r\n<strong>&nbsp; Nam ch&acirc;m v&agrave; từ t&iacute;nh .</strong><br />\r\nTrong tự nhi&ecirc;n c&oacute; một số chất c&oacute; thể h&uacute;t được sắt gọi l&agrave; nam ch&acirc;m tự nhi&ecirc;n.<br />\r\nTrong c&ocirc;ng nghiệm người ta luyện th&eacute;p hoặc hợp chất th&eacute;p để tạo th&agrave;nh nam ch&acirc;m nh&acirc;n tạo.<br />\r\nNam ch&acirc;m lu&ocirc;n lu&ocirc;n c&oacute; hai cực l&agrave; cực bắc North (N) v&agrave; cực nam South (S) , nếu chặt thanh nam ch&acirc;m ra l&agrave;m 2 th&igrave; ta lại được hai nam ch&acirc;m mới cũng c&oacute; hai cực N v&agrave; S &ndash; đ&oacute; l&agrave; nam ch&acirc;m c&oacute; t&iacute;nh chất kh&ocirc;ng ph&acirc;n chia..<br />\r\nNam ch&acirc;m thường được ứng dụng để sản xuất loa điện động, micro hoặc m&ocirc; tơ DC.<br />\r\n<br />\r\n<strong>&nbsp; Từ trường&nbsp;</strong><br />\r\nTừ trường l&agrave; v&ugrave;ng kh&ocirc;ng gian xung quanh nam ch&acirc;m c&oacute; t&iacute;nh chất truyền lực từ l&ecirc;n c&aacute;c vật liệu c&oacute; từ t&iacute;nh, từ trường l&agrave; tập hợp của c&aacute;c đường sức đi từ Bắc đến cực nam.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/namcham1.gif" style="height:124px; margin:0px; width:232px" /></div>\r\n\r\n<p><strong>&nbsp; Cường độ từ trường&nbsp;</strong><br />\r\nL&agrave; đại lượng đặc trưng cho độ mạnh yếu của từ trường, k&yacute; hiệu l&agrave; H đơn vị l&agrave; A/m</p>\r\n\r\n<p><strong>&nbsp; Độ từ cảm</strong><br />\r\nL&agrave; đại lượng đặc trưng cho vật c&oacute; từ t&iacute;nh chịu t&aacute;c động của từ trường, độ từ cảm phụ thuộc v&agrave;o vật liệu . VD&nbsp; Sắt c&oacute; độ từ cảm mạnh hơn đồng nhiều lần . Độ từ cảm được t&iacute;nh bởi c&ocirc;ng thức</p>\r\n\r\n<p>B = &micro;.H</p>\r\n\r\n<p>Trong đ&oacute; B : l&agrave;&nbsp; độ từ cảm<br />\r\n&micro; : l&agrave; độ từ thẩm<br />\r\nH :&nbsp; l&agrave; cường độ từ trường</p>\r\n\r\n<p><strong>&nbsp; Từ th&ocirc;ng</strong><br />\r\nL&agrave; số đường sức đi qua một đơn vị diện t&iacute;ch, từ th&ocirc;ng tỷ lệ thuật với cường độ từ trường.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/tuthong.png" style="height:131px; margin:0px; width:232px" /></div>\r\n\r\n<p><strong>&nbsp; Ứng dụng của Nam ch&acirc;m vĩnh cửu.</strong><br />\r\nNam ch&acirc;m vĩnh cửu được ứng dụng nhiều trong thiết bị điện tử, ch&uacute;ng được d&ugrave;ng để sản xuất Loa, Micro v&agrave; c&aacute;c loại M&ocirc; tơ DC.</p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px"><img alt="Bài 4: Khái niệm về từ trường" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/speaker.png" style="height:120px; margin:0px; width:120px" /></div>', 'PIN', 0, 'Có một cách để biến PIN một chiều thành 2 chiều. Nếu làm đúng theo những hướng dẫn trong bài này, các bạn có thể thực hiện thành công mà không sợ nguy hiểm đến tính mạng.'),
(24, 'Làm sao biết PIN nào tốt?', '<p><strong>Mạch nghịch lưu d&ugrave;ng 4047 Haicong.vn đ&atilde; lắp nhiều rồi , thường được ưu điểm xung ra ổn định nhưng nhược điểm l&agrave; ic kh&ocirc;ng chịu được d&ograve;ng cao hay ch&aacute;y.</strong></p>\r\n\r\n<div style="background:transparent; border:0px; padding:0px">\r\n<div style="background:rgb(241, 241, 241); border:0px; padding:8px">\r\n<p><img alt="Mạch inverter 500W dùng transistor đơn giản" src="http://hc.serveftp.com/uploads/dien-tu/2012_11/acqui3.jpg" style="height:181px; margin:0px; width:450px" /></p>\r\n\r\n<p>&nbsp;Ngược lại với th&igrave; tranistor tỏ ra kh&aacute; ổn định ,dễ lắp , đặc biệt&nbsp;Haicong.vn đ&atilde; test thử 2 mạch tr&ecirc;n 1 biến &aacute;p th&igrave; c&ocirc;ng suất của tran lớn hơn . Đ&acirc;y l&agrave; mạch nghịch lưu 500W . lưu &yacute; : biến &aacute;p sơ cấp quấn d&acirc;y 3mm &aacute;p t&acirc;m 11 V , biến trở 100K để chỉnh đ&uacute;ng tần số 50Hz , tran Cs D718X5 mỗi vế , th&ecirc;m mạch lọc LC .Ch&uacute;c c&aacute;c bạn th&agrave;nh c&ocirc;ng !</p>\r\n</div>\r\n</div>', 'PIN', 0, 'Trên thị trường hiện này có rất nhiều loại PIN với nhiều chúng loại và mẫu mã. Tuy nhiên nếu không cẩn thận có thể gặp phải tình trạng mua phải PIN Trung Quốc kém chất lượng. Bằng một vài thủ thuật và mẹo nhỏ, bạn có thể biết PIN nào tốt, PIN nào không tốt.');

