  `intro_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `intro_content` longtext NOT NULL,
  PRIMARY KEY (`intro_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;

